(2, 0, 2, 2, 1, 'decoder.py', '<pre contenteditable="false">import pickle\r\n\r\nfrom bitarray import bitarray\r\n\r\nfrom utils import EncodeResult\r\n\r\n\r\nclass HuffmanDecoder:\r\n    def get_text_from_result(self, encoded_result: EncodeResult) -&gt; bitarray:\r\n        text = bitarray()\r\n        text.frombytes(encoded_result.encoded_result)\r\n        return text[:encoded_result.length]\r\n\r\n    def decode(self, encoded_result: EncodeResult):\r\n        text = self.get_text_from_result(encoded_result)\r\n        tmp = encoded_result.root\r\n        result = ""\r\n        for ch in text:\r\n            if str(ch) == "0":\r\n                tmp = tmp.left\r\n            else:\r\n                tmp = tmp.right\r\n            if tmp.left is None and tmp.right is None:\r\n                result += tmp.value\r\n                tmp = encoded_result.root\r\n        return result\r\n\r\n    def read(self, filepath: str) -&gt; EncodeResult:\r\n        with open(filepath, ''rb'') as file:\r\n            obj = pickle.load(file)\r\n        return obj</pre>', '2024-06-16 12:41:52', '2024-06-16 12:41:52', 0, 0, 0, 1, 0, '', '', 0, -1),
(3, 0, 2, 3, 1, 'encoder.py', '<pre contenteditable="false">import heapq\r\nfrom collections import Counter\r\nfrom typing import Optional\r\n\r\nfrom utils import Node, EncodeResult\r\n\r\n\r\nclass HuffmanEncoder:\r\n    def get_symbols_frequency(self, text: str) -&gt; dict[str, int]:\r\n        result = Counter(text)\r\n        return result\r\n\r\n    def build_tree(self, text: str):\r\n        queue = [\r\n            Node(value=key, frequency=value, left=None, right=None) for (key, value) in self.get_symbols_frequency(text).items()\r\n        ]\r\n        heapq.heapify(queue)\r\n\r\n        while len(queue) &gt; 1:\r\n            left = heapq.heappop(queue)\r\n            right = heapq.heappop(queue)\r\n            new_node = Node(\r\n                left=left,\r\n                right=right,\r\n                frequency=left.frequency + right.frequency,\r\n                value=None\r\n            )\r\n            heapq.heappush(queue, new_node)\r\n\r\n        root = queue[0]\r\n        return root\r\n\r\n    def get_codes_wrapper(self, root: Optional[Node], current_code: str, codes: dict):\r\n        if root is None:\r\n            return\r\n\r\n        if root.value is not None:\r\n            codes[root.value] = current_code\r\n            return\r\n        self.get_codes_wrapper(root.left, current_code + "0", codes)\r\n        self.get_codes_wrapper(root.right, current_code + "1", codes)\r\n\r\n    def get_codes(self, text: str) -&gt; str:\r\n        result = ""\r\n        codes = {}\r\n        self.get_codes_wrapper(\r\n            self.build_tree(text),\r\n            "",\r\n            codes\r\n        )\r\n        for ch in text:\r\n            result += codes[ch]\r\n        return result\r\n\r\n    def encode(self, text: str) -&gt; EncodeResult:\r\n        return EncodeResult(root=self.build_tree(text), encoded_result=self.get_codes(text))\r\n\r\n\r\n</pre>', '2024-06-16 12:42:10', '2024-06-16 12:42:10', 0, 0, 0, 1, 0, '', '', 0, -1),
(4, 0, 2, 4, 1, 'main.py', '<pre contenteditable="false">import os\r\n\r\nfrom encoder import HuffmanEncoder\r\nfrom decoder import HuffmanDecoder\r\nfrom utils import DataSource, MEGABYTE\r\n\r\n\r\ndef encode(filepath):\r\n    output_filepath = input("Введите путь к файлу для кодирования: ")\r\n    encoder = HuffmanEncoder()\r\n    data_source = DataSource()\r\n    data = data_source.read_file(filepath)\r\n    result = encoder.encode(data)\r\n    data_source.write_result(result, output_filepath)\r\n    print("Файл упешно закодирован!")\r\n    print(f"Вес исходного файла - {os.path.getsize(filepath) / MEGABYTE} Mb")\r\n    print(f"Вес выходного файла - {os.path.getsize(output_filepath) / MEGABYTE} Mb")\r\n\r\n\r\ndef decode(filepath):\r\n    output_filepath = input("Введите путь к файлу для раскодирования: ")\r\n    decoder = HuffmanDecoder()\r\n    data_source = DataSource()\r\n    result = decoder.decode(decoder.read(filepath))\r\n    data_source.write_file(result, output_filepath)\r\n    print("Файл успешно разкодирован!")\r\n\r\n\r\noperation_type = input("Введите тип операции[encode/decode]: ")\r\nfilepath = input("Введите путь к файлу: ")\r\noperations = {\r\n    "encode": encode,\r\n    "decode": decode\r\n}\r\noperations[operation_type](filepath)\r\n\r\n</pre>', '2024-06-16 12:42:31', '2024-06-16 12:42:31', 0, 0, 0, 1, 0, '', '', 0, -1),
