(47, 0, 3, 47, 1, 'journal/users/controllers.py', '<pre contenteditable="false">from profiles.models import Anthropometric, Functional, PhysicalStandards\r\nfrom .models import CustomUser\r\n\r\nfrom journal.settings import FIRST_SEMESTER, COUNT_SEMESTER\r\n\r\n\r\ndef generate_physical_tables(user: CustomUser) -&gt; None:\r\n    for semester in range(FIRST_SEMESTER, COUNT_SEMESTER + 1):\r\n        anthropometric_record = Anthropometric(user=user, semester=semester)\r\n        anthropometric_record.save()\r\n        functional_record = Functional(user=user, semester=semester)\r\n        functional_record.save()\r\n        physical_record = PhysicalStandards(user=user, semester=semester)\r\n        physical_record.save()</pre>', '2024-06-16 14:55:20', '2024-06-16 14:55:20', 0, 0, 0, 1, 0, '', '', 0, -1),
(48, 0, 3, 48, 1, 'journal/users/forms.py', '<pre contenteditable="false">from django.contrib.auth.forms import UserCreationForm\r\nfrom django import forms\r\nfrom django.forms import ModelForm, EmailInput, PasswordInput, TextInput\r\n\r\nfrom .controllers import generate_physical_tables\r\nfrom .models import CustomUser, StudentGroup\r\nimport datetime\r\n\r\n\r\nclass RegisterForm(UserCreationForm):\r\n    first_name = forms.CharField(\r\n        label=''Имя'',\r\n        widget=forms.TextInput(attrs={''class'': ''form-control'', ''placeholder'': ''Имя''})\r\n    )\r\n\r\n    last_name = forms.CharField(\r\n        label=''Фамилия'',\r\n        widget=forms.TextInput(attrs={''class'': ''form-control'', ''placeholder'': ''Фамилия''})\r\n    )\r\n\r\n    email = forms.EmailField(\r\n        label=''Email'',\r\n        widget=forms.EmailInput(attrs={''class'': ''form-control'', ''placeholder'': ''Email'', ''type'': ''Email''})\r\n    )\r\n\r\n    password1 = forms.CharField(\r\n        label=''Пароль'',\r\n        widget=forms.TextInput(attrs={''class'': ''form-control'', ''type'': ''password'', ''placeholder'': ''Пароль''})\r\n    )\r\n\r\n    password2 = forms.CharField(\r\n        label=''Подтверждение пароля'',\r\n        widget=forms.TextInput(attrs={''class'': ''form-control'', ''type'': ''password'', ''placeholder'': ''Пароль''})\r\n    )\r\n\r\n    join_year = forms.IntegerField(\r\n        label=''Год поступления'',\r\n        widget=forms.TextInput(attrs={''class'': ''form-control'', ''placeholder'': ''Год поступления(пример 2021)''}),\r\n        max_value=datetime.date.today().year\r\n    )\r\n\r\n    group = forms.ModelChoiceField(\r\n        label="Учебная группа",\r\n        queryset=StudentGroup.objects.all(),\r\n        widget=forms.Select(attrs={''class'': ''form-control'', ''placeholder'': ''Учебная группа''}),\r\n        required=True\r\n    )\r\n\r\n    gender = forms.ChoiceField(\r\n        label="Пол",\r\n        choices=[("male", "Мужской"), ("wooman", "Женский")],\r\n        widget=forms.Select(attrs={''class'': ''form-control'', ''placeholder'': ''Пол''})\r\n    )\r\n\r\n    birth_year = forms.IntegerField(\r\n        label="Год рождения",\r\n        widget=forms.DateTimeInput(attrs={''class'': ''form-control'', ''placeholder'': ''Год рождения''})\r\n    )\r\n\r\n    def save(self, commit=True):\r\n        user = super(RegisterForm, self).save(commit=False)\r\n        fields = CustomUser._meta.fields\r\n        for field in fields:\r\n            try:\r\n                setattr(user, field.name, self.cleaned_data[field.name])\r\n            except KeyError:\r\n                ...\r\n        user.username = user.email\r\n        generate_physical_tables(user)\r\n        if commit:\r\n            user.save()\r\n        return user\r\n\r\n    class Meta:\r\n        fields = ("first_name", "last_name", "password1", "password2", "email")\r\n        model = CustomUser\r\n\r\n\r\nclass LoginForm(ModelForm):\r\n    class Meta:\r\n        model = CustomUser\r\n        fields = ("email", "password")\r\n        widgets = {\r\n            "email": TextInput({''class'': ''form-control'', ''placeholder'': ''Email''}),\r\n            "password": PasswordInput({''class'': ''form-control'', ''type'': ''password'', ''placeholder'': ''Пароль''})\r\n        }\r\n\r\n</pre>', '2024-06-16 14:55:34', '2024-06-16 14:55:34', 0, 0, 0, 1, 0, '', '', 0, -1),
(49, 0, 3, 49, 1, 'journal/users/misc.py', '<pre contenteditable="false">from dataclasses import dataclass\r\n\r\n\r\n@dataclass\r\nclass EntityNotFound(Exception):\r\n    db_id: int\r\n\r\n    @property\r\n    def message(self):\r\n        raise NotImplementedError\r\n\r\n\r\nclass GroupNotFound(EntityNotFound):\r\n    @property\r\n    def message(self) -&gt; str:\r\n        return f"Группа с ID {self.db_id} не найдена"\r\n\r\n\r\nclass UserNotFound(EntityNotFound):\r\n    @property\r\n    def message(self) -&gt; str:\r\n        return f"Пользователь с ID {self.db_id} не найден"</pre>', '2024-06-16 14:55:54', '2024-06-16 14:55:54', 0, 0, 0, 1, 0, '', '', 0, -1),
(50, 0, 3, 50, 1, 'journal/users/models.py', '<pre contenteditable="false">import datetime\r\n\r\nfrom django.contrib.auth.base_user import BaseUserManager\r\nfrom django.contrib.auth.models import AbstractUser\r\nfrom django.db import models\r\n\r\n\r\nclass StudentGroup(models.Model):\r\n    title = models.CharField(max_length=256, blank=True, verbose_name="Название учебной группы", unique=True)\r\n\r\n    def __str__(self):\r\n        return self.title\r\n\r\n\r\nclass CustomUser(AbstractUser):\r\n    first_name = models.CharField(max_length=256, blank=True, verbose_name="Имя")\r\n    last_name = models.CharField(max_length=256, blank=True, verbose_name="Фамилия")\r\n    group = models.ForeignKey(StudentGroup, on_delete=models.CASCADE)\r\n    birth_year = models.IntegerField()\r\n    join_year = models.IntegerField()\r\n    email = models.EmailField(unique=True)\r\n    gender = models.CharField(default="male")\r\n\r\n    def __str__(self):\r\n        return f"{self.last_name} {self.first_name}"\r\n\r\n    def get_age_caption(self) -&gt; str:\r\n        age_token = lambda age_to: (\r\n                (age_to in range(5, 20)) and ''лет'' or\r\n                (1 in (age_to, (diglast := age_to % 10))) and ''год'' or\r\n                ({age_to, diglast} &amp; {2, 3, 4}) and ''года'' or ''лет'')\r\n        age = datetime.datetime.now().year - self.birth_year\r\n        return f"{age} {age_token(age)}"\r\n\r\n    def get_age(self) -&gt; int:\r\n        age = datetime.datetime.now().year - self.birth_year\r\n        return age\r\n\r\n    def get_course(self) -&gt; int:\r\n        return datetime.datetime.now().year - self.join_year + 1\r\n</pre>', '2024-06-16 14:56:09', '2024-06-16 14:56:09', 0, 0, 0, 1, 0, '', '', 0, -1),
(51, 0, 3, 51, 1, 'journal/users/services.py', '<pre contenteditable="false">from typing import Any\r\n\r\nfrom users.models import CustomUser, StudentGroup\r\nfrom .misc import UserNotFound, GroupNotFound\r\n\r\n\r\nclass UserService:\r\n    def get(self, id: int) -&gt; CustomUser:\r\n        user = CustomUser.objects.filter(id=id).first()\r\n        if user is None:\r\n            raise UserNotFound(db_id=id)\r\n        return user\r\n\r\n    def get_group(self, id: int) -&gt; StudentGroup:\r\n        group = StudentGroup.objects.filter(id=id).first()\r\n        if group is None:\r\n            raise GroupNotFound(db_id=id)\r\n        return group\r\n\r\n    def edit_group(self, id: int, group_id: int):\r\n        user = self.get(id)\r\n        group = self.get_group(group_id)\r\n        user.group = group\r\n        user.save()</pre>', '2024-06-16 14:56:29', '2024-06-16 14:56:29', 0, 0, 0, 1, 0, '', '', 0, -1),
