(53, 0, 3, 53, 1, 'journal/users/views.py', '<pre contenteditable="false">from django.contrib.auth import authenticate, login, logout\r\nfrom django.http import HttpRequest\r\nfrom django.shortcuts import render, redirect\r\nfrom django.views import View\r\nfrom django.views.generic import TemplateView\r\n\r\nfrom .forms import RegisterForm, LoginForm\r\nfrom .controllers import generate_physical_tables\r\n\r\n\r\nclass MainView(TemplateView, View):\r\n    template_name = ''index.html''\r\n\r\n    def get(self, request: HttpRequest, *args, **kwargs):\r\n        return self.render_to_response({\r\n            "user": request.user,\r\n            "title": "Journal"\r\n        })\r\n\r\n\r\nclass RegisterView(TemplateView, View):\r\n    template_name = "register.html"\r\n    title = "Регистрация"\r\n\r\n    def get(self, request, *args, **kwargs):\r\n        form = RegisterForm()\r\n        return self.render_to_response({\r\n            "form": form,\r\n            "title": self.title\r\n        })\r\n\r\n    def post(self, request, *args, **kwargs):\r\n        form = RegisterForm(data=request.POST)\r\n        if form.is_valid():\r\n            user = form.save()\r\n            login(request, user)\r\n            return redirect("main")\r\n        else:\r\n            return self.render_to_response({"form": form})\r\n\r\n\r\nclass LoginView(TemplateView, View):\r\n    template_name = "login.html"\r\n    title = "Вход"\r\n\r\n    def get(self, request, *args, **kwargs):\r\n        form = LoginForm()\r\n        return self.render_to_response({\r\n            "form": form,\r\n            "title": self.title\r\n        })\r\n\r\n    def post(self, request, *args, **kwargs):\r\n        user = authenticate(username=request.POST.get("email"),\r\n                            password=request.POST.get("password"))\r\n        if user is not None:\r\n            if user.is_active:\r\n                login(request, user)\r\n\r\n        return redirect("main")\r\n\r\n\r\ndef logout_endpoint(request):\r\n    logout(request)\r\n    return redirect("main")</pre>', '2024-06-16 14:57:02', '2024-06-16 14:57:02', 0, 0, 0, 1, 0, '', '', 0, -1),
