(60, 0, 4, 60, 1, 'core/storage/models.h', '<pre contenteditable="false">//\r\n// Created by Ренат Якублевич on 27.12.23.\r\n//\r\n\r\n#ifndef CASH_MODELS_H\r\n#define CASH_MODELS_H\r\n\r\n#include &lt;iostream&gt;\r\n#include &lt;typeinfo&gt;\r\n\r\nclass TypeValidator {\r\npublic:\r\n    bool IsNumeric(const std::string&amp; str) {\r\n        try {\r\n            std::size_t pos = 0;\r\n            std::stoi(str, &amp;pos);\r\n            return pos == str.length();\r\n        } catch (...) {\r\n            return false;\r\n        }\r\n    }\r\n\r\n    void router(std::string object, std::string type) {\r\n        if (type == "int" &amp;&amp; !IsNumeric(object)) {\r\n            throw std::runtime_error("error");\r\n        }\r\n        throw std::runtime_error("error");\r\n    }\r\n};\r\n\r\nclass Model {\r\npublic:\r\n    Model() {\r\n        this-&gt;object = nullptr;\r\n        this-&gt;type = "";\r\n    }\r\n\r\n    Model(std::string object, std::string type) {\r\n        this-&gt;object = object;\r\n        this-&gt;type = type;\r\n//        validate_type();\r\n    }\r\n\r\n    std::string get_object() {\r\n        return object;\r\n    }\r\n\r\n    void validate_type() {\r\n        TypeValidator validator;\r\n        try {\r\n            validator.router(object, type);\r\n        } catch(...) {\r\n            throw std::runtime_error("error");\r\n        }\r\n    }\r\n\r\nprivate:\r\n    std::string object;\r\n    std::string type;\r\n};\r\n\r\n\r\n#endif //CASH_MODELS_H</pre>', '2024-06-16 15:00:35', '2024-06-16 15:00:35', 0, 0, 0, 1, 0, '', '', 0, -1),
