(62, 0, 4, 62, 1, 'core/storage/storage.h', '<pre contenteditable="false">//\r\n// Created by Ренат Якублевич on 27.12.23.\r\n//\r\n\r\n#ifndef CASH_STORAGE_H\r\n#define CASH_STORAGE_H\r\n\r\n#include &lt;iostream&gt;\r\n#include &lt;map&gt;\r\n#include &lt;any&gt;\r\n#include "models.h"\r\n\r\n\r\nclass Storage {\r\npublic:\r\n    ~Storage() {\r\n\r\n    }\r\n\r\n    void set(std::string key, Model* value) {\r\n        this-&gt;items[key] = value;\r\n    }\r\n\r\n    Model* get(std::string key) {\r\n        if (items.count(key)) return items[key];\r\n        return nullptr;\r\n    }\r\n\r\nprivate:\r\n    std::map&lt;std::string, Model*&gt; items;\r\n};\r\n\r\n\r\n#endif //CASH_STORAGE_H</pre>', '2024-06-16 15:01:05', '2024-06-16 15:01:05', 0, 0, 0, 1, 0, '', '', 0, -1),
