(68, 0, 5, 68, 1, 'auth_service/database/database.py', '<pre contenteditable="false">from sqlalchemy import MetaData, Table\r\nfrom sqlalchemy.ext.declarative import declarative_base\r\nfrom sqlalchemy.orm import sessionmaker\r\nfrom sqlalchemy.ext.asyncio import create_async_engine\r\nfrom sqlalchemy.ext.asyncio import async_sessionmaker\r\n\r\nfrom config import config\r\n\r\n\r\nengine = create_async_engine(\r\n    config.database_url\r\n)\r\nSessionLocal = async_sessionmaker(autocommit=False, autoflush=False, bind=engine)\r\nBase = declarative_base()</pre>', '2024-06-16 16:00:02', '2024-06-16 16:00:02', 0, 0, 0, 1, 0, '', '', 0, -1),
(69, 0, 5, 69, 1, 'auth_service/database/models.py', '<pre contenteditable="false">from sqlalchemy import Boolean, Column, ForeignKey, Integer, String, JSON\r\nfrom sqlalchemy.orm import relationship, Relationship, mapped_column, Mapped\r\n\r\nfrom .database import Base, engine\r\n\r\n\r\nclass User(Base):\r\n    __tablename__ = "users"\r\n\r\n    id: Mapped[int] = mapped_column(primary_key=True, index=True)\r\n    username: Mapped[str] = mapped_column(String(100), unique=True, index=True)\r\n    password: Mapped[str] = mapped_column(String(100))</pre>', '2024-06-16 16:00:19', '2024-06-16 16:00:19', 0, 0, 0, 1, 0, '', '', 0, -1),
