mt = %H:%M:%S</pre>', '2024-06-16 16:01:19', '2024-06-16 16:01:19', 0, 0, 0, 1, 0, '', '', 0, -1),
(74, 0, 5, 74, 1, 'auth_service/config.py', '<pre contenteditable="false">from datetime import timedelta\r\n\r\nfrom pydantic_settings import BaseSettings, SettingsConfigDict\r\nfrom pydantic import SecretStr, Field\r\nfrom dotenv import load_dotenv\r\n\r\nimport os\r\n\r\n\r\nclass Config(BaseSettings):\r\n    auth_secret_key: str = Field()\r\n    hash_algorithm: str = Field()\r\n    expiration_time: int = Field()\r\n    database_url: str = Field()\r\n    token_expire: timedelta\r\n    sentry_url: str = Field()\r\n\r\n\r\nload_dotenv()\r\nconfig = Config(\r\n    auth_secret_key=os.getenv("AUTH_SECRET_KEY"),\r\n    hash_algorithm="HS256",\r\n    expiration_time=1800,\r\n    database_url=os.getenv("DATABASE_URL"),\r\n    token_expire=timedelta(minutes=30),\r\n    sentry_url=os.getenv("SENTRY_URL")\r\n)</pre>', '2024-06-16 16:01:35', '2024-06-16 16:01:35', 0, 0, 0, 1, 0, '', '', 0, -1),
(75, 0, 5, 75, 1, 'auth_service/exceptions.py', '<pre contenteditable="false">from fastapi import HTTPException\r\n\r\n\r\nclass UserException(HTTPException):\r\n    ...\r\n\r\n\r\nclass UserNotFound(UserException):\r\n    def __init__(self, detail: str):\r\n        super().__init__(\r\n            status_code=400,\r\n            detail=detail\r\n        )\r\n\r\n\r\nclass IncorrectPassword(UserException):\r\n    def __init__(self, detail: str):\r\n        super().__init__(\r\n            status_code=400,\r\n            detail=detail\r\n        )\r\n\r\n\r\nclass UserExists(UserException):\r\n    def __init__(self, detail: str):\r\n        super().__init__(\r\n            status_code=403,\r\n            detail=detail\r\n        )</pre>', '2024-06-16 16:01:49', '2024-06-16 16:01:49', 0, 0, 0, 1, 0, '', '', 0, -1);
