(76, 0, 5, 76, 1, 'auth_service/main.py', '<pre contenteditable="false">from datetime import timedelta\r\nfrom typing import Union, Annotated\r\n\r\nimport sentry_sdk\r\nfrom fastapi import FastAPI, Depends\r\nfrom fastapi.security import OAuth2PasswordBearer, OAuth2PasswordRequestForm\r\nfrom passlib.context import CryptContext\r\nfrom pydantic import BaseModel\r\nfrom sqlalchemy.ext.asyncio import AsyncSession\r\nfrom sqlalchemy.orm import Session\r\n\r\nfrom config import config\r\nimport schemas\r\nfrom database.utils import get_session\r\nfrom services.user_service import UserService, UserCRUD\r\n\r\n\r\nsentry_sdk.init(\r\n    dsn=config.sentry_url,\r\n    # Set traces_sample_rate to 1.0 to capture 100%\r\n    # of transactions for performance monitoring.\r\n    traces_sample_rate=1.0,\r\n    # Set profiles_sample_rate to 1.0 to profile 100%\r\n    # of sampled transactions.\r\n    # We recommend adjusting this value in production.\r\n    profiles_sample_rate=1.0,\r\n)\r\napp = FastAPI()\r\n\r\n\r\n@app.post("/token")\r\nasync def login_with_token(\r\n    user_in: schemas.UserIn,\r\n    session: AsyncSession = Depends(get_session)\r\n):\r\n    user_service = UserService(\r\n        UserCRUD,\r\n        session\r\n    )\r\n\r\n    await user_service.authenticate_user(user_in)\r\n    access_token = user_service.create_access_token(\r\n        data={"sub": user_in.username}\r\n    )\r\n    return schemas.Token(access_token=access_token, token_type="bearer")\r\n\r\n\r\n@app.post("/register")\r\nasync def register(\r\n    user_in: schemas.UserIn,\r\n    session: AsyncSession = Depends(get_session)\r\n):\r\n    user_service = UserService(\r\n        UserCRUD,\r\n        session\r\n    )\r\n    await user_service.register(user_in)\r\n    return {"success": True}</pre>', '2024-06-16 16:02:05', '2024-06-16 16:02:05', 0, 0, 0, 1, 0, '', '', 0, -1);
