(88, 0, 6, 88, 1, 'collection.cpp', '<pre contenteditable="false">//\r\n// Created by Renat Yakublevich on 20.11.23.\r\n//\r\n#include "collection.h"\r\n\r\ntemplate&lt;typename T&gt;\r\nCollection&lt;T&gt;::Collection() {\r\n    this-&gt;root = new Tree&lt;T&gt;;\r\n    this-&gt;count = 0;\r\n}\r\n\r\ntemplate&lt;typename T&gt;\r\nEntities&lt;T&gt; Collection&lt;T&gt;::select_all() {\r\n    T entities[MAX_COUNT_ENTITIES];\r\n    int index = 0;\r\n    root-&gt;dfs(entities, index, this-&gt;root);\r\n    for (int i = 1; i &lt; index; ++i) {\r\n        entities[i - 1] = entities[i];\r\n    }\r\n    return Entities&lt;T&gt;(entities, index - 1);\r\n}\r\n\r\n\r\ntemplate&lt;typename T&gt;\r\nvoid Collection&lt;T&gt;::insert(T object) {\r\n    count++;\r\n    this-&gt;root-&gt;add(object);\r\n}\r\n\r\ntemplate&lt;typename T&gt;\r\nT* Collection&lt;T&gt;::select(T object) {\r\n    return root-&gt;search(object)-&gt;GetObject();\r\n}\r\n\r\ntemplate&lt;typename T&gt;\r\nvoid Collection&lt;T&gt;::del(T object) {\r\n    count--;\r\n    root = root-&gt;remove(object, root);\r\n}\r\n\r\n\r\ntemplate&lt;typename T&gt;\r\nTree&lt;T&gt;::Tree() {\r\n    this-&gt;left = nullptr;\r\n    this-&gt;right = nullptr;\r\n}\r\n\r\ntemplate&lt;typename T&gt;\r\nvoid Tree&lt;T&gt;::dfs(T* entities, int&amp; index, Tree&lt;T&gt;* node) {\r\n    if (node == nullptr) return;\r\n    dfs(entities, index, node-&gt;left);\r\n    entities[index] = *node-&gt;GetObject();\r\n    index += 1;\r\n    dfs(entities, index, node-&gt;right);\r\n}\r\n\r\ntemplate&lt;typename T&gt;\r\nTree&lt;T&gt;::Tree(T object) {\r\n    this-&gt;object = object;\r\n    this-&gt;left = nullptr;\r\n    this-&gt;right = nullptr;\r\n}\r\n\r\ntemplate&lt;typename T&gt;\r\nTree&lt;T&gt;::~Tree() {\r\n    delete left;\r\n    delete right;\r\n}\r\n\r\n\r\ntemplate&lt;typename T&gt;\r\nT* Tree&lt;T&gt;::GetObject()\r\n{\r\n    return &amp;object;\r\n}\r\n\r\n\r\ntemplate&lt;typename T&gt;\r\nvoid Tree&lt;T&gt;::add(T value) {\r\n    if (value &lt; object) {\r\n        if (left == nullptr) {\r\n            left = new Tree(value);\r\n        }\r\n        else {\r\n            left-&gt;add(value);\r\n        }\r\n    }\r\n    else if (value &gt; object) {\r\n        if (right == nullptr) {\r\n            right = new Tree(value);\r\n        }\r\n        else {\r\n            right-&gt;add(value);\r\n        }\r\n    }\r\n}\r\n\r\ntemplate&lt;typename T&gt;\r\nTree&lt;T&gt;* Tree&lt;T&gt;::search(T value) {\r\n    if (value == object) {\r\n        return this;\r\n    }\r\n    else if (value &lt; object) {\r\n        if (left == nullptr) {\r\n            return nullptr;\r\n        }\r\n        else {\r\n            return left-&gt;search(value);\r\n        }\r\n    }\r\n    else {\r\n        if (right == nullptr) {\r\n            return nullptr;\r\n        }\r\n        else {\r\n            return right-&gt;search(value);\r\n        }\r\n    }\r\n}\r\n\r\ntemplate&lt;typename T&gt;\r\nTree&lt;T&gt;* Tree&lt;T&gt;::findMinNode(Tree* root) {\r\n    while (root-&gt;left != nullptr) {\r\n        root = root-&gt;left;\r\n    }\r\n    return root;\r\n}\r\n\r\ntemplate&lt;typename T&gt;\r\nTree&lt;T&gt;* Tree&lt;T&gt;::remove(T value, Tree* root) {\r\n    if (root == nullptr) {\r\n        return nullptr;\r\n    }\r\n\r\n    if (value &lt; *root-&gt;GetObject()) {\r\n        root-&gt;left = remove(value, root-&gt;left);\r\n    } else if (value &gt; *root-&gt;GetObject()) {\r\n        root-&gt;right = remove(value, root-&gt;right);\r\n    } else {\r\n        if (root-&gt;left == nullptr &amp;&amp; root-&gt;right == nullptr) {\r\n            delete root;\r\n            root = nullptr;\r\n        } else if (root-&gt;left == nullptr) {\r\n            Tree* temp = root;\r\n            root = root-&gt;right;\r\n            delete temp;\r\n        } else if (root-&gt;right == nullptr) {\r\n            Tree* temp = root;\r\n            root = root-&gt;left;\r\n            delete temp;\r\n        } else {\r\n            Tree* minNode = findMinNode(root-&gt;right);\r\n            root-&gt;object = minNode-&gt;object;\r\n            root-&gt;right = remove(*minNode-&gt;GetObject(),root-&gt;right);\r\n        }\r\n    }\r\n    return root;\r\n}\r\n\r\ntemplate &lt;typename T&gt;\r\nvoid Entities&lt;T&gt;::sort() {\r\n    int left = 0;\r\n    int right = length - 1;\r\n    bool swapped = false;\r\n\r\n    do {\r\n        swapped = false;\r\n\r\n        for (int i = left; i &lt; right; ++i) {\r\n            if (entities[i] &gt; entities[i + 1]) {\r\n                std::swap(entities[i], entities[i + 1]);\r\n                swapped = true;\r\n            }\r\n        }\r\n\r\n        if (!swapped) {\r\n            break;\r\n        }\r\n\r\n        --right;\r\n        for (int i = right; i &gt; left; --i) {\r\n            if (entities[i] &lt; entities[i - 1]) {\r\n                std::swap(entities[i], entities[i - 1]);\r\n                swapped = true;\r\n            }\r\n        }\r\n\r\n        ++left;\r\n    } while (swapped);\r\n}\r\n\r\n#include "models.h"\r\n\r\ntemplate class Entities&lt;User&gt;;\r\ntemplate class Entities&lt;Artist&gt;;\r\ntemplate class Entities&lt;std::string&gt;;\r\n\r\ntemplate Tree&lt;User&gt;* Tree&lt;User&gt;::remove(User value, Tree* root);\r\ntemplate Tree&lt;Artist&gt;* Tree&lt;Artist&gt;::remove(Artist value, Tree* root);\r\n\r\ntemplate Tree&lt;User&gt;* Tree&lt;User&gt;::findMinNode(Tree* root);\r\ntemplate Tree&lt;Artist&gt;* Tree&lt;Artist&gt;::findMinNode(Tree* root);\r\n\r\ntemplate Tree&lt;User&gt;* Tree&lt;User&gt;::search(User value);\r\ntemplate Tree&lt;Artist&gt;* Tree&lt;Artist&gt;::search(Artist value);\r\n\r\ntemplate void Tree&lt;User&gt;::add(User value);\r\ntemplate void Tree&lt;Artist&gt;::add(Artist value);\r\n\r\ntemplate User* Tree&lt;User&gt;::GetObject();\r\ntemplate Artist* Tree&lt;Artist&gt;::GetObject();\r\n\r\ntemplate Tree&lt;User&gt;::~Tree();\r\ntemplate Tree&lt;Artist&gt;::~Tree();\r\n\r\ntemplate Tree&lt;User&gt;::Tree(User object);\r\ntemplate Tree&lt;Artist&gt;::Tree(Artist object);\r\n\r\ntemplate void Tree&lt;Artist&gt;::dfs(Artist* entities, int&amp; index, Tree&lt;Artist&gt;* node);\r\ntemplate void Tree&lt;User&gt;::dfs(User* entities, int&amp; index, Tree&lt;User&gt;* node);\r\n\r\ntemplate Tree&lt;User&gt;::Tree();\r\ntemplate Tree&lt;Artist&gt;::Tree();\r\n\r\ntemplate void Collection&lt;User&gt;::del(User object);\r\ntemplate void Collection&lt;Artist&gt;::del(Artist object);\r\n\r\ntemplate User* Collection&lt;User&gt;::select(User object);\r\ntemplate Artist* Collection&lt;Artist&gt;::select(Artist object);\r\n\r\ntemplate void Collection&lt;User&gt;::insert(User object);\r\ntemplate void Collection&lt;Artist&gt;::insert(Artist object);\r\n\r\ntemplate Entities&lt;User&gt; Collection&lt;User&gt;::select_all();\r\ntemplate Entities&lt;Artist&gt; Collection&lt;Artist&gt;::select_all();\r\n\r\ntemplate Collection&lt;User&gt;::Collection();\r\ntemplate Collection&lt;Artist&gt;::Collection();\r\n\r\n</pre>', '2024-06-16 16:06:00', '2024-06-16 16:06:00', 0, 0, 0, 1, 0, '', '', 0, -1),
(89, 0, 6, 89, 1, 'collection.h', '<pre contenteditable="false">//\r\n// Created by Renat Yakublevich on 18.11.23.\r\n//\r\n\r\n#ifndef KURS_COLLECTION_H\r\n#define KURS_COLLECTION_H\r\n\r\n#define MAX_COUNT_ENTITIES 1028\r\n\r\n#include &lt;iostream&gt;\r\n\r\ntemplate&lt;typename T&gt;\r\nclass Tree\r\n{\r\nprivate:\r\n    Tree* left;\r\n    Tree* right;\r\n    T object;\r\npublic:\r\n\r\n    Tree();\r\n\r\n    Tree(T object);\r\n\r\n    ~Tree();\r\n\r\n    T* GetObject();\r\n\r\n    void add(T value);\r\n\r\n    Tree* search(T value);\r\n\r\n    Tree* remove(T object, Tree* root);\r\n\r\n    void dfs(T* entities, int&amp; index, Tree&lt;T&gt;* node);\r\n\r\n    Tree* findMinNode(Tree* root);\r\n};\r\n\r\ntemplate &lt;typename T&gt;\r\nclass Entities {\r\npublic:\r\n    T* entities;\r\n    int length;\r\n\r\n    Entities(T* entities, int length);\r\n    Entities();\r\n\r\n    void sort();\r\n//    ~Entities();\r\n};\r\n\r\ntemplate&lt;typename T&gt;\r\nEntities&lt;T&gt;::Entities(T* entities, int length) : entities(entities), length(length) {};\r\n\r\ntemplate&lt;typename T&gt;\r\nEntities&lt;T&gt;::Entities() {}\r\n\r\n\r\ntemplate&lt;typename T&gt;\r\nclass Collection {\r\npublic:\r\n    Collection();\r\n\r\n    Entities&lt;T&gt; select_all();\r\n\r\n    void insert(T object);\r\n\r\n    T* select(T object);\r\n\r\n    void del(T object);\r\nprivate:\r\n    Tree&lt;T&gt;* root;\r\n    int count;\r\n};\r\n\r\n\r\n\r\n#endif //KURS_COLLECTION_H</pre>', '2024-06-16 16:06:18', '2024-06-16 16:06:18', 0, 0, 0, 1, 0, '', '', 0, -1),
(90, 0, 6, 90, 1, 'controllers.cpp', '<pre contenteditable="false">//\r\n// Created by Renat Yakublevich on 20.11.23.\r\n//\r\n\r\n#include "controllers.h"\r\n\r\nEntities&lt;Artist&gt; search_by_year(Entities&lt;Artist&gt; artists, int left_range, int right_range) {\r\n    Artist* new_artists = new Artist[MAX_COUNT_ENTITIES];\r\n    int index = 0;\r\n\r\n    for (int i = 0; i &lt; artists.length; ++i) {\r\n        if (artists.entities[i].get_year() &gt;= left_range &amp;&amp; artists.entities[i].get_year() &lt;= right_range) {\r\n            new_artists[index] = artists.entities[i];\r\n            index++;\r\n        }\r\n    }\r\n    return {new_artists, index};\r\n}\r\n\r\nEntities&lt;Artist&gt; get_instrument_tier_list(Entities&lt;Artist&gt;* artists, std::string instruemnt) {\r\n    Artist* new_artists = new Artist[3];\r\n    int index = 0;\r\n    sort_by_place(artists);\r\n    for (int i = 0; i &lt; artists-&gt;length; ++i) {\r\n        if (index == 2) break;\r\n        if (artists-&gt;entities[i].get_instrument() == instruemnt) {\r\n            new_artists[index] = artists-&gt;entities[i];\r\n            index++;\r\n        }\r\n    }\r\n    return {new_artists, index};\r\n}\r\n\r\nEntities&lt;std::string&gt; get_all_instruments(Entities&lt;Artist&gt; artists) {\r\n    std::string* instruments = new string[MAX_COUNT_ENTITIES];\r\n    int index = 0;\r\n\r\n    for (int i = 0; i &lt; artists.length; ++i) {\r\n        if (!str_exists(artists.entities[i].get_instrument(), instruments, index)) {\r\n            instruments[index] = artists.entities[i].get_instrument();\r\n            index++;\r\n        }\r\n    }\r\n    return {instruments, index};\r\n}</pre>', '2024-06-16 16:06:33', '2024-06-16 16:06:33', 0, 0, 0, 1, 0, '', '', 0, -1),
(91, 0, 6, 91, 1, 'controllers.h', '<pre contenteditable="false">//\r\n// Created by Renat Yakublevich on 20.11.23.\r\n//\r\n\r\n#ifndef KURS_CONTROLLERS_H\r\n#define KURS_CONTROLLERS_H\r\n\r\n#include "models.h"\r\n#include "misc.h"\r\n\r\nEntities&lt;Artist&gt; search_by_year(Entities&lt;Artist&gt; artists, int left_range, int right_range);\r\n\r\nEntities&lt;std::string&gt; get_all_instruments(Entities&lt;Artist&gt; artists);\r\n\r\nEntities&lt;Artist&gt; get_instrument_tier_list(Entities&lt;Artist&gt;* artists, std::string instruemnt);\r\n\r\n\r\n#endif //KURS_CONTROLLERS_H</pre>', '2024-06-16 16:06:47', '2024-06-16 16:06:47', 0, 0, 0, 1, 0, '', '', 0, -1),
(92, 0, 6, 92, 1, 'database.cpp', '<pre contenteditable="false">//\r\n// Created by Renat Yakublevich on 20.11.23.\r\n//\r\n#include "database.hpp"\r\n\r\ntemplate &lt;typename T&gt;\r\nUser* Database&lt;T&gt;::select(std::string in_login, std::string in_password) {\r\n    std::ifstream database;\r\n    database.open(this-&gt;file_path);\r\n    std::string buffer;\r\n    std::string user_login;\r\n    std::string user_password;\r\n    User* current_user;\r\n    if (database.is_open())\r\n    {\r\n\r\n        while (std::getline(database, buffer))\r\n        {\r\n            current_user = serialize(buffer);\r\n            if (current_user-&gt;get_login() == in_login &amp;&amp; current_user-&gt;get_password() == in_password) {\r\n                if (current_user-&gt;is_admin()) return new Admin(*current_user);\r\n                return new Customer(*current_user);\r\n            }\r\n        }\r\n    }\r\n    database.close();\r\n    return nullptr;\r\n}\r\n\r\n\r\ntemplate &lt;typename T&gt;\r\nvoid Database&lt;T&gt;::insert(T object) {\r\n    std::ofstream file;\r\n    file.open(this-&gt;file_path, std::ios::out | std::ios::app);\r\n    if (file.is_open())\r\n    {\r\n        file &lt;&lt; object.serialize(object, this-&gt;separator) &lt;&lt; std::endl;\r\n    }\r\n\r\n    file.close();\r\n}\r\n\r\ntemplate &lt;typename T&gt;\r\nvoid Database&lt;T&gt;::clear() {\r\n    std::ofstream ofs;\r\n    ofs.open(file_path, std::ofstream::out | std::ofstream::trunc);\r\n    ofs.close();\r\n}\r\n\r\ntemplate &lt;typename T&gt;\r\nT* Database&lt;T&gt;::serialize(std::string line) {\r\n    return T::deserialize(line, this-&gt;separator);\r\n}\r\n\r\n\r\ntemplate &lt;typename T&gt;\r\nCollection&lt;T&gt;* Database&lt;T&gt;::select_all() {\r\n    std::ifstream database;\r\n    database.open(this-&gt;file_path);\r\n    std::string buffer;\r\n    auto* collection = new Collection&lt;T&gt;;\r\n    int user_index = 0;\r\n    if (database.is_open())\r\n    {\r\n        while (std::getline(database, buffer))\r\n        {\r\n            if (strlen(buffer.c_str()) &lt;= 5) continue;\r\n            collection-&gt;insert(*serialize(buffer));\r\n            user_index++;\r\n        }\r\n    }\r\n    database.close();\r\n    return collection;\r\n}\r\n\r\n\r\ntemplate Artist* Database&lt;Artist&gt;::serialize(std::string line);\r\ntemplate User* Database&lt;User&gt;::serialize(std::string line);\r\n\r\ntemplate User* Database&lt;User&gt;::select(std::string in_login, std::string in_password);\r\n\r\ntemplate Collection&lt;User&gt;* Database&lt;User&gt;::select_all();\r\ntemplate Collection&lt;Artist&gt;* Database&lt;Artist&gt;::select_all();\r\n\r\ntemplate void Database&lt;User&gt;::clear();\r\ntemplate void Database&lt;Artist&gt;::clear();\r\n\r\ntemplate void Database&lt;User&gt;::insert(User object);\r\ntemplate void Database&lt;Artist&gt;::insert(Artist object);</pre>', '2024-06-16 16:07:01', '2024-06-16 16:07:01', 0, 0, 0, 1, 0, '', '', 0, -1),
(93, 0, 6, 93, 1, 'database.hpp', '<pre contenteditable="false">#ifndef KURS_DATABASE_HPP\r\n#define KURS_DATABASE_HPP\r\n\r\n#include "models.h"\r\n#include &lt;iostream&gt;\r\n#include &lt;fstream&gt;\r\n\r\n\r\ntemplate&lt;typename T&gt;\r\nclass Database {\r\npublic:\r\n    void insert(T object);\r\n\r\n    User* select(std::string in_login, std::string in_password);\r\n\r\n    T* serialize(std::string line);\r\n\r\n    Collection&lt;T&gt;* select_all();\r\n\r\n    void clear();\r\n\r\n    Database(const std::string file_path) : file_path(file_path) {};\r\n\r\nprivate:\r\n    std::string file_path;\r\n    std::string extension;\r\n    const char* separator = ";";\r\n    const int MAX_RECORDS = 1028;\r\n};\r\n\r\n\r\n#endif //KURS_DATABASE_HPP</pre>', '2024-06-16 16:07:17', '2024-06-16 16:07:17', 0, 0, 0, 1, 0, '', '', 0, -1),
