(125, 0, 8, 125, 1, 'tree/menu/services.py', '<pre contenteditable="false">from django.db.models import QuerySet\r\n\r\nfrom .models import MenuItem, Menu\r\n\r\n\r\nclass Crud:\r\n    @staticmethod\r\n    def get_menu_items(title: str) -&gt; QuerySet[MenuItem]:\r\n        menu_items = MenuItem.objects.filter(menu__title=title).order_by("parent__id").all()\r\n\r\n        return menu_items\r\n\r\n\r\nclass Services:\r\n    @staticmethod\r\n    def _group_dfs(groups: dict, item: MenuItem, items: list[MenuItem]):\r\n        tmp_item = item\r\n        parents = []\r\n        while tmp_item.parent:\r\n            parents.append(tmp_item.parent)\r\n            tmp_item = tmp_item.parent\r\n        tmp_groups = groups\r\n        for parent in parents[::-1]:\r\n            if parent not in tmp_groups:\r\n                return items.append(item)\r\n            tmp_groups = tmp_groups[parent]\r\n        tmp_groups[item] = {}\r\n\r\n    @staticmethod\r\n    def group_menu_items(items: list[MenuItem]) -&gt; dict:\r\n        groups = {}\r\n        for item in items:\r\n            Services._group_dfs(groups, item, items)\r\n        return groups</pre>', '2024-06-16 16:53:53', '2024-06-16 16:53:53', 0, 0, 0, 1, 0, '', '', 0, -1),
