(157, 0, 9, 157, 1, 'core/api/CodeVersusAPI/CodeVersusAPI/exceptions.py', '<pre contenteditable="false">from rest_framework.views import exception_handler\r\nfrom rest_framework.response import Response\r\n\r\n\r\nclass CustomBaseException(Exception):\r\n    ...\r\n\r\n\r\nclass ServiceException(CustomBaseException):\r\n    status_code = 500\r\n    message = "Service Error"\r\n\r\n\r\nclass NotFoundException(CustomBaseException):\r\n    status_code = 404\r\n    message = "Not Found"\r\n\r\n\r\nclass InvalidEmail(CustomBaseException):\r\n    status_code = 500\r\n    message = "Invalid email"\r\n\r\n\r\ndef custom_exception_handler(exc, context):\r\n    response = exception_handler(exc, context)\r\n    if issubclass(type(exc), CustomBaseException):\r\n        data = {"details": exc.message}\r\n        response = Response(data, status=exc.status_code)\r\n\r\n    return response</pre>', '2024-06-16 17:04:08', '2024-06-16 17:04:08', 0, 0, 0, 1, 0, '', '', 0, -1),
