(167, 0, 9, 167, 1, 'core/api/CodeVersusAPI/questions/models.py', '<pre contenteditable="false">from django.db import models\r\n\r\n\r\nclass Question(models.Model):\r\n    title = models.CharField(\r\n        max_length=4096,\r\n        verbose_name="Заголовок вопроса"\r\n    )\r\n\r\n\r\nclass Answer(models.Model):\r\n    title = models.CharField(\r\n        max_length=4096,\r\n        verbose_name="Заголовок ответа"\r\n    )\r\n    question = models.ForeignKey(Question, on_delete=models.CASCADE)\r\n</pre>', '2024-06-16 17:34:13', '2024-06-16 17:34:13', 0, 0, 0, 1, 0, '', '', 0, -1),
(168, 0, 9, 168, 1, 'core/api/CodeVersusAPI/questions/serializers.py', '<pre contenteditable="false">from rest_framework import serializers\r\n\r\nfrom .models import Question, Answer\r\n\r\n\r\nclass QuestionSerializer(serializers.ModelSerializer):\r\n    class Meta:\r\n        model = Question\r\n        fields = ["__all__"]\r\n\r\n\r\nclass AnswerSerializer(serializers.ModelSerializer):\r\n    class Meta:\r\n        model = Answer\r\n        fields = ["id", "title", "question"]</pre>', '2024-06-16 17:34:37', '2024-06-16 17:34:37', 0, 0, 0, 1, 0, '', '', 0, -1),
(169, 0, 9, 169, 1, 'core/api/CodeVersusAPI/questions/services.py', '<pre contenteditable="false">from .ai_service import AIHandler\r\nfrom .models import Question, Answer\r\nfrom CodeVersusAPI.exceptions import NotFoundException\r\n\r\n\r\nai_handler = AIHandler()\r\n\r\n\r\nclass QuestionService:\r\n    @staticmethod\r\n    def generate_question(language: str):\r\n        prompt = ai_handler.send_prompt("question", language)\r\n        new_question = Question(\r\n            title=prompt.split("Вопрос:")[1].split("Ответ")[0].strip()\r\n        )\r\n        new_question.save()\r\n        new_answer = Answer(\r\n            title=prompt.split("Ответ:")[1].strip().rstrip(),\r\n            question=new_question\r\n        )\r\n        new_answer.save()\r\n        return {\r\n            "id": new_question.pk,\r\n            "title": new_question.title\r\n        }\r\n\r\n    @staticmethod\r\n    def get_answer(question_id: int):\r\n        try:\r\n            answer = Answer.objects.get(question_id=question_id)\r\n        except Answer.DoesNotExist:\r\n            raise NotFoundException\r\n        print(answer.title)\r\n        return {\r\n            "title": answer.title\r\n        }\r\n\r\n</pre>', '2024-06-16 17:34:55', '2024-06-16 17:34:55', 0, 0, 0, 1, 0, '', '', 0, -1),
