(207, 0, 9, 207, 1, 'core/test_system/rpc_server.py', '<pre contenteditable="false">import logging\r\nimport sys\r\n\r\nimport Pyro4\r\n\r\nfrom test_storage import TestStorage\r\nfrom test_system import Task\r\nfrom models import Test\r\n\r\n\r\nlogging.basicConfig(stream=sys.stdout, level=logging.DEBUG)\r\nlogging.debug(''This will be logged in Docker Compose logs'')\r\n\r\n\r\ntest_storage = TestStorage()\r\n\r\n\r\n@Pyro4.expose\r\nclass TaskExecutor:\r\n    def solve(self, task_id: str | int, code: str, time_limit: int) -&gt; dict:\r\n        tests = test_storage.get_tests_from_task(task_id=int(task_id))\r\n        task = Task(tests, time_limit)\r\n        task.run_tests(code)\r\n        result = {\r\n            "status": task.count_failed_tests == 0,\r\n            "result": [test.__dict__ for test in task.tests]\r\n        }\r\n        return result\r\n\r\n    def add_test(self, test: dict) -&gt; None:\r\n        test_storage.insert_test(test)\r\n\r\n\r\nlogging.debug(''This will be logged in Docker Compose logs'')\r\n\r\nPyro4.Daemon.serveSimple({\r\n    TaskExecutor: ''TaskExecutor'',\r\n}, host="0.0.0.0", port=9090, ns=False, verbose=True)</pre>', '2024-06-17 19:56:33', '2024-06-17 19:56:33', 0, 0, 0, 1, 0, '', '', 0, -1),
(208, 0, 9, 208, 1, 'core/test_system/test_storage.py', '<pre contenteditable="false">from typing import Dict, List\r\n\r\nfrom pymongo import MongoClient\r\n\r\nfrom models import Test\r\n\r\n\r\nclass TestStorage:\r\n    def __init__(self):\r\n        print(1)\r\n        self.client = MongoClient("mongodb://mongo:27017/")\r\n        self.db = self.client["tests"]\r\n        self.collection = self.db["series"]\r\n\r\n    def insert_test(self, test: Dict[str, str]) -&gt; str:\r\n        return self.collection.insert_one(test).inserted_id\r\n\r\n    def get_tests_from_task(self, task_id: int) -&gt; List[Test]:\r\n        tests = self.collection.find({"task_id": task_id})\r\n        return [Test(**test) for test in tests]</pre>', '2024-06-17 20:05:40', '2024-06-17 20:05:40', 0, 0, 0, 1, 0, '', '', 0, -1);
