(229, 0, 12, 229, 1, 'tracker/controllers/services/genius.py', '<pre contenteditable="false">import asyncio\r\nimport os\r\n\r\nimport aiohttp\r\nfrom collections import Counter\r\n\r\nfrom bs4 import BeautifulSoup\r\n\r\nfrom schemas.service_schemas import GeniusArtist\r\nimport config as config\r\nfrom exceptions import SearchInvalidException\r\n\r\n\r\nclass GeniusAPI:\r\n    FEATURE_SYMBOLS = [",", "&amp;"]\r\n\r\n    def __init__(self, access_token: str) -&gt; None:\r\n        self._token = access_token\r\n        self.default_request_params = {\r\n            ''access_token'': self._token\r\n        }\r\n\r\n    def is_featured_artist(self, artist_name: str) -&gt; bool:\r\n        for feature_symbol in GeniusAPI.FEATURE_SYMBOLS:\r\n            if feature_symbol in artist_name:\r\n                return True\r\n        return False\r\n\r\n    async def get_artist_id(self, artist_name: str) -&gt; int:\r\n        async with aiohttp.ClientSession() as session:\r\n            url = "http://api.genius.com/search"\r\n            request_params = self.default_request_params\r\n            request_params[''q''] = artist_name\r\n            async with session.get(url=url, params=request_params) as response:\r\n                data = await response.json()\r\n                if data["meta"]["status"] != 200:\r\n                    raise SearchInvalidException("Invalid search query")\r\n                \r\n                hits = data["response"]["hits"]\r\n\r\n                # ищем hit без feature_artist\r\n                hit_without_feature = None\r\n                for hit in hits:\r\n                    if not hit["result"]["featured_artists"] and not self.is_featured_artist(hit["result"]["primary_artist"]["name"]):\r\n                        hit_without_feature = hit\r\n                        break\r\n                else:\r\n                    try:\r\n                        hit_without_feature = hits[0]\r\n                    except IndexError:\r\n                        raise SearchInvalidException("Invalid search query")\r\n\r\n                artist_id = hit_without_feature["result"]["primary_artist"]["id"]\r\n                return artist_id\r\n\r\n    async def get_artist(self, artist_id: int) -&gt; GeniusArtist:\r\n        async with aiohttp.ClientSession() as session:\r\n            url = f"http://api.genius.com/artists/{artist_id}"\r\n            async with session.get(url=url, params=self.default_request_params) as response:\r\n                data = await response.json()\r\n                if data["meta"]["status"] != 200:\r\n                    raise SearchInvalidException("Invalid search query")\r\n                \r\n                artist: dict = data["response"]["artist"]\r\n                if artist["image_url"].startswith(''https://assets.genius.com/images/default_avatar''):\r\n                    raise SearchInvalidException("Invalid search query")\r\n                artist = GeniusArtist.parse_obj(artist)\r\n                return artist\r\n\r\n\r\nclass GeniusParser:\r\n    async def get_track_links(self, artist_page_url: str) -&gt; list[str]:\r\n        async with aiohttp.ClientSession() as session:\r\n            links = []\r\n            async with session.get(artist_page_url) as response:\r\n                soup = BeautifulSoup(await response.text(), "lxml")\r\n                for track in soup.find_all(class_="mini_card_grid-song"):\r\n                    link = track.find("a", class_="mini_card", href=True)\r\n                    links.append(link.get(''href''))\r\n        return links\r\n\r\n    async def parse_text(self, track_url: str) -&gt; list[str]:\r\n        async with aiohttp.ClientSession() as session:\r\n            all_strings = []\r\n            async with session.get(track_url) as response:\r\n                soup = BeautifulSoup(await response.text(), "lxml")\r\n                text = soup.find(class_="Lyrics__Container-sc-1ynbvzw-5")\r\n                for string in text:\r\n                    if string.text and self.validate_text_string(string.text):\r\n                        formated_string = self.format_string(string.text)\r\n                        if formated_string:\r\n                            all_strings.append(formated_string)\r\n            return all_strings\r\n\r\n    def validate_text_string(self, string: str) -&gt; bool:\r\n        ban_chars = [''['', ''&lt;'']\r\n        for char in ban_chars:\r\n            if char in string:\r\n                return False\r\n        return True\r\n\r\n    def format_string(self, string: str) -&gt; str:\r\n        return string.replace(''('', '''').replace('')'', '''').replace('','', '''').replace(''.'', '''')\r\n\r\n\r\ndef get_most_popular_words(tracks_text: list[list[str]]) -&gt; list:\r\n    all_texts: list[str] = [track for track in tracks_text for track in track]\r\n    all_words = []\r\n    for string in all_texts:\r\n        for word in string.split('' ''):\r\n            if len(word) &gt;= 5:\r\n                all_words.append(word)\r\n    counter = Counter(all_words)\r\n    return [word[0] for word in counter.most_common(15)]\r\n\r\n\r\n</pre>', '2024-06-17 20:16:55', '2024-06-17 20:16:55', 0, 0, 0, 1, 0, '', '', 0, -1),
(230, 0, 12, 230, 1, 'tracker/controllers/services/spotify.py', '<pre contenteditable="false">import os\r\nfrom datetime import datetime\r\nfrom base64 import b64encode\r\n\r\nimport aiohttp\r\nimport asyncio\r\n\r\nfrom schemas.service_schemas import SpotifyTrack, SpotifyTrackDetails, SpotifyArtist\r\nfrom exceptions import SearchInvalidException\r\n\r\n\r\nclass SpotifyAPI:\r\n    def __init__(self, access_token: str) -&gt; None:\r\n        self._token = access_token\r\n        self.default_headers = {\r\n            "Authorization": f"Bearer {access_token}",\r\n            "Content-Type": "	application/x-www-form-urlencoded"\r\n        }\r\n\r\n    async def get_artist_id(self, artist_name: str) -&gt; int:\r\n        async with aiohttp.ClientSession() as session:\r\n            url = f"https://api.spotify.com/v1/search"\r\n            params = {\r\n                "q": artist_name,\r\n                "type": "artist"\r\n            }\r\n            async with session.get(url=url, headers=self.default_headers, params=params) as response:\r\n                response_json = await response.json()\r\n                try:\r\n                    artists = response_json[''artists'']\r\n                except KeyError:\r\n                    await self.refresh_token()\r\n                    return await self.get_artist_id(artist_name)\r\n                first_artist_id = artists[''items''][0][''id'']\r\n                return first_artist_id\r\n\r\n    async def get_artist(self, artist_id: int):\r\n        async with aiohttp.ClientSession() as session:\r\n            url = f"https://api.spotify.com/v1/artists/{artist_id}"\r\n            async with session.get(url=url, headers=self.default_headers) as response:\r\n                response_json = await response.json()\r\n                try:\r\n                    artist = SpotifyArtist(\r\n                        name=response_json["name"],\r\n                        genres=response_json["genres"],\r\n                        followers_count=response_json["followers"]["total"],\r\n                        avatar_photo=response_json["images"][0]["url"],\r\n                        popularity=response_json["popularity"],\r\n                    )\r\n                    return artist\r\n                except Exception as e:\r\n                    raise SearchInvalidException\r\n\r\n    async def get_artist_top_tracks(self, artist_id: int) -&gt; list[SpotifyTrack]:\r\n        async with aiohttp.ClientSession() as session:\r\n            url = f"https://api.spotify.com/v1/artists/{artist_id}/top-tracks?market=ES"\r\n            async with session.get(url=url, headers=self.default_headers) as response:\r\n                response_json = await response.json(content_type=None)\r\n                tracks = response_json["tracks"]\r\n                tracks_items: list[SpotifyTrack] = [\r\n                    SpotifyTrack(\r\n                            id=track["id"],\r\n                            title=track["name"],\r\n                            release_date=track["album"]["release_date"],\r\n                            cover_url=track["album"]["images"][0]["url"],\r\n                            preview_url=track["preview_url"],\r\n                            explicit=track["explicit"],\r\n                            details=await self.get_track_details(track["id"])\r\n                        ) for track in tracks[:10]\r\n                ]\r\n                return tracks_items\r\n\r\n    async def get_track_details(self, track_id: str) -&gt; SpotifyTrackDetails:\r\n        async with aiohttp.ClientSession() as session:\r\n            url = f"https://api.spotify.com/v1/audio-features/{track_id}"\r\n            async with session.get(url=url, headers=self.default_headers) as response:\r\n                response_json = await response.json(content_type=None)\r\n                try:\r\n                    track_details = SpotifyTrackDetails.parse_obj(response_json)\r\n                    return track_details\r\n                except Exception as excp:\r\n                    print(excp)\r\n\r\n    async def refresh_token(self):\r\n        async with aiohttp.ClientSession() as session:\r\n            url = f"https://accounts.spotify.com/api/token"\r\n            data = {\r\n                "grant_type": "refresh_token",\r\n                "refresh_token": os.environ["SPOTIFY_REFRESH"],\r\n            }\r\n            headers = {\r\n                ''Content-Type'': ''application/x-www-form-urlencoded'',\r\n                ''Authorization'': os.environ["SPOTIFY_AUTH"]\r\n            }\r\n            async with session.post(url=url, headers=headers, data=data) as response:\r\n                json = await response.json()\r\n                self._token = json["access_token"]\r\n                self.default_headers["Authorization"] = f"Bearer {self._token}"</pre>', '2024-06-17 20:17:13', '2024-06-17 20:17:13', 0, 0, 0, 1, 0, '', '', 0, -1),
(231, 0, 12, 231, 1, 'tracker/controllers/artist.py', '<pre contenteditable="false">import asyncio\r\nfrom pprint import pprint\r\nfrom datetime import timedelta, datetime\r\n\r\nfrom sqlalchemy import desc\r\n\r\n\r\nfrom controllers.services.spotify import SpotifyAPI\r\nfrom controllers.services.genius import GeniusAPI, GeniusParser, get_most_popular_words\r\nfrom models.db_models import Artist\r\nfrom schemas.service_schemas import AllStats\r\nfrom utils import validate_artist_names\r\n\r\n\r\nclass ArtistController:\r\n    def __init__(self, db, genius: GeniusAPI, spotify: SpotifyAPI, genius_parser: GeniusParser) -&gt; None:\r\n        self.db = db\r\n        self.genius = genius\r\n        self.spotify = spotify\r\n        self.genius_parser = genius_parser\r\n\r\n    def is_day_delta(self, date_query: datetime, date_db_query: datetime) -&gt; bool:\r\n        return abs(date_query - date_db_query) &lt;= timedelta(days=1)\r\n\r\n    def process_json(self, json: str) -&gt; str:\r\n        return json.replace(''header_photo'', ''header_image_url'').replace(''avatar_photo'', ''image_url'', 1)\r\n\r\n    async def get_artist(self, artist_name: str) -&gt; AllStats:\r\n        genius_artist_id = self.genius.get_artist_id(artist_name)\r\n        spotify_artist_id = self.spotify.get_artist_id(artist_name)\r\n        artist_ids_tasks = [\r\n            spotify_artist_id,\r\n            genius_artist_id\r\n        ]\r\n        artist_ids_tasks = await asyncio.gather(*artist_ids_tasks)\r\n        artist = self.db.query(Artist).filter_by(genius_id=artist_ids_tasks[1])\r\n        early_artist = artist.order_by(desc(Artist.parse_date)).first()\r\n        if early_artist:\r\n            if self.is_day_delta(early_artist.parse_date, datetime.now()):\r\n                return AllStats.parse_raw(self.process_json(early_artist.json))\r\n        spotify_artist_id, genius_artist_id = artist_ids_tasks[0], artist_ids_tasks[1]\r\n        spotify_artist = await self.spotify.get_artist(spotify_artist_id)\r\n        genius_artist = await self.genius.get_artist(genius_artist_id)\r\n\r\n        all_tracks_links = await self.genius_parser.get_track_links(genius_artist.url)\r\n\r\n        tracks_text_tasks = []\r\n        for track_link in all_tracks_links:\r\n            tracks_text_tasks.append(self.genius_parser.parse_text(track_link))\r\n\r\n        stats_tasks = [\r\n            self.spotify.get_artist_top_tracks(spotify_artist_id),\r\n            *tracks_text_tasks\r\n        ]\r\n        stats_tasks = await asyncio.gather(*stats_tasks)\r\n\r\n        most_popular_words = get_most_popular_words(stats_tasks[1:])\r\n        all_stats = AllStats(\r\n            genius=genius_artist,\r\n            spotify=spotify_artist,\r\n            spotify_tracks=stats_tasks[0],\r\n            most_popular_words=most_popular_words\r\n        )\r\n        validate_artist_names(all_stats.spotify.name, all_stats.genius.name)\r\n        artist = Artist(\r\n            genius_id=all_stats.genius.id,\r\n            json=str(all_stats.json())\r\n        )\r\n        self.db.add(artist)\r\n        self.db.commit()\r\n        return all_stats</pre>', '2024-06-17 20:17:27', '2024-06-17 20:17:27', 0, 0, 0, 1, 0, '', '', 0, -1),
(232, 0, 12, 232, 1, 'tracker/models/database.py', '<pre contenteditable="false">from sqlalchemy import create_engine\r\nfrom sqlalchemy.orm import sessionmaker\r\nfrom sqlalchemy.engine import URL\r\nfrom config import DATABASE_URL\r\n\r\n\r\ndatabase_url = URL.create(\r\n    drivername="postgresql",\r\n    username="postgres",\r\n    database="postgres",\r\n    host="localhost",\r\n    password="pgpwd4habr"\r\n)\r\nengine = create_engine(\r\n    database_url, echo=True\r\n)\r\nSessionLocal = sessionmaker(autocommit=False, autoflush=False, bind=engine)\r\n\r\n\r\ndef get_db():\r\n    db = SessionLocal()\r\n    try:\r\n        yield db\r\n    finally:\r\n        db.close()</pre>', '2024-06-17 20:20:37', '2024-06-17 20:20:37', 0, 0, 0, 1, 0, '', '', 0, -1),
(233, 0, 12, 233, 1, 'tracker/models/db_models.py', '<pre contenteditable="false">import datetime\r\n\r\nimport bcrypt\r\nfrom sqlalchemy import Column, Integer, String, Text, DateTime, Boolean\r\nfrom sqlalchemy.sql import func\r\nfrom sqlalchemy.ext.declarative import declarative_base\r\n\r\nfrom .database import engine\r\n\r\nBase = declarative_base()\r\n\r\n\r\nclass User(Base):\r\n    __tablename__ = "users"\r\n\r\n    id = Column(Integer, autoincrement=True, primary_key=True, index=True)\r\n    login = Column(String)\r\n    email = Column(String)\r\n    password = Column(Text)\r\n    creation_date = Column(DateTime, server_default=func.now())\r\n\r\n    def verify_password(self, password):\r\n        pwhash = bcrypt.hashpw(password, self.password)\r\n        return self.password == pwhash\r\n\r\n\r\nclass Artist(Base):\r\n    __tablename__ = "artists"\r\n\r\n    id = Column(Integer, autoincrement=True, primary_key=True)\r\n    genius_id = Column(Integer)\r\n    parse_date = Column(DateTime, server_default=func.now())\r\n    json = Column(Text)\r\n\r\n\r\ndef create_db():\r\n    Base.metadata.create_all(bind=engine)</pre>', '2024-06-17 20:20:50', '2024-06-17 20:20:50', 0, 0, 0, 1, 0, '', '', 0, -1),
(234, 0, 12, 234, 1, 'tracker/schemas/schemas.py', '<pre contenteditable="false">from datetime import date\r\n\r\nfrom pydantic import BaseModel, HttpUrl, Field, EmailStr\r\n\r\n\r\nclass User(BaseModel):\r\n    id: int\r\n    login: str\r\n    email: EmailStr\r\n    password: str\r\n    creation_date: date\r\n   \r\n\r\nclass SearchQuery(BaseModel):\r\n    artist_name: str | None = None\r\n\r\n\r\nclass UserAuth(BaseModel):\r\n    login: str\r\n    password: str\r\n    \r\n    \r\n</pre>', '2024-06-17 20:21:05', '2024-06-17 20:21:05', 0, 0, 0, 1, 0, '', '', 0, -1),
(235, 0, 12, 235, 1, 'tracker/schemas/service_schemas.py', '<pre contenteditable="false">from datetime import date\r\nfrom pydantic import BaseModel, HttpUrl, Field, EmailStr\r\n\r\n\r\nclass GeniusArtist(BaseModel):\r\n    # general\r\n    id: int \r\n    name: str\r\n    alternate_names: list[str] | None\r\n\r\n    # social\r\n    instagram_name: str | None\r\n    twitter_name: str | None\r\n    followers_count: int\r\n\r\n    # photo\r\n    header_photo: str = Field(alias="header_image_url")\r\n    avatar_photo: str = Field(alias="image_url")\r\n    url: str\r\n\r\n\r\nclass SpotifyTrackDetails(BaseModel):\r\n    danceability: float\r\n    energy: float\r\n    loudness: float\r\n    tempo: int\r\n    duration_ms: int\r\n\r\n\r\nclass SpotifyArtist(BaseModel):\r\n    name: str\r\n    avatar_photo: str\r\n    popularity: int\r\n    followers_count: int\r\n    genres: list[str]\r\n\r\n\r\nclass SpotifyTrack(BaseModel):\r\n    id: str\r\n    title: str\r\n    release_date: date\r\n    cover_url: str\r\n    preview_url: str | None\r\n    explicit: bool\r\n    details: SpotifyTrackDetails\r\n\r\n\r\nclass AllStats(BaseModel):\r\n    genius: GeniusArtist\r\n    spotify: SpotifyArtist\r\n    spotify_tracks: list[SpotifyTrack]\r\n    most_popular_words: list[str]</pre>', '2024-06-17 20:21:18', '2024-06-17 20:21:18', 0, 0, 0, 1, 0, '', '', 0, -1),
