(238, 0, 12, 238, 1, 'tracker/logger.py', '<pre contenteditable="false">import sys\r\nfrom loguru import logger\r\n\r\n\r\nclass Logger:\r\n    def __init__(self):\r\n        self.logger = logger\r\n        self.logger.add("log.log", format="{time} {level} {message}", level="WARNING")\r\n        self.logger.add(sys.stdout, format="{message}", level="INFO")\r\n\r\n    def __new__(cls, *args, **kwargs):\r\n        if not hasattr(cls, ''instance''):\r\n            cls.instance = super(Logger, cls).__new__(cls)\r\n        return cls.instance\r\n\r\n    def log_info(self, message: str):\r\n        self.logger.info(message)</pre>', '2024-06-17 20:22:00', '2024-06-17 20:22:00', 0, 0, 0, 1, 0, '', '', 0, -1);
