(239, 0, 12, 239, 1, 'tracker/main.py', '<pre contenteditable="false">import asyncio\r\n\r\nimport uvicorn\r\nfrom fastapi import FastAPI, Response, Depends\r\nfrom fastapi.middleware.cors import CORSMiddleware\r\n\r\nfrom schemas.schemas import SearchQuery, UserAuth\r\nfrom models.database import get_db\r\n\r\nfrom controllers.services.genius import GeniusAPI, GeniusParser\r\nfrom controllers.services.spotify import SpotifyAPI\r\n\r\nimport config\r\nimport exceptions\r\nfrom controllers.artist import ArtistController\r\nfrom models.database import SessionLocal\r\nfrom logger import Logger\r\n\r\napp = FastAPI()\r\n\r\norigins = ["*"]\r\n\r\napp.add_middleware(\r\n    CORSMiddleware,\r\n    allow_origins=origins,\r\n    allow_credentials=True,\r\n    allow_methods=origins,\r\n    allow_headers=origins,\r\n)\r\n\r\ngenius = GeniusAPI(config.GENIUS_ACCESS_TOKEN)\r\ngenius_parser = GeniusParser()\r\nspotify = SpotifyAPI(config.SPOTIFY_ACCESS_TOKEN)\r\nartist_controller = ArtistController(\r\n    db=SessionLocal(),\r\n    spotify=spotify,\r\n    genius=genius,\r\n    genius_parser=genius_parser\r\n)\r\nlogger = Logger()\r\n\r\n\r\n@app.post(''/'')\r\nasync def get_search_query(search_query: SearchQuery, db = Depends(get_db)):\r\n    try:\r\n        artist = await artist_controller. get_artist(search_query.artist_name)\r\n    except exceptions.SearchInvalidException as error_msg:\r\n        return {"error": str(error_msg)}\r\n    except Exception as error_msg:\r\n        logger.log_info(error_msg)\r\n        return {"error": "Server error"}\r\n    return artist.json()\r\n\r\n\r\n@app.post(''/login'')\r\nasync def login(user_auth: UserAuth):\r\n    ...\r\n\r\nif __name__ == "__main__":\r\n    uvicorn.run(app, host="0.0.0.0", port=8001)</pre>', '2024-06-17 20:22:12', '2024-06-17 20:22:12', 0, 0, 0, 1, 0, '', '', 0, -1),
(240, 0, 12, 240, 1, 'tracker/utils.py', '<pre contenteditable="false">from difflib import SequenceMatcher\r\nfrom exceptions import SearchInvalidException\r\nfrom logger import Logger\r\n\r\n\r\nlogger = Logger()\r\n\r\n\r\ndef validate_artist_names(*artist_names: list[str]) -&gt; None:\r\n    for idx in range(len(artist_names)):\r\n        equal_factor_artist_names = SequenceMatcher(None, artist_names[idx - 1], artist_names[idx]).ratio()\r\n        logger.log_info(equal_factor_artist_names)\r\n        if equal_factor_artist_names &lt;= 0.2:\r\n            raise SearchInvalidException("Invalid search query")\r\n\r\n</pre>', '2024-06-17 20:22:24', '2024-06-17 20:22:24', 0, 0, 0, 1, 0, '', '', 0, -1),
