(254, 0, 13, 254, 1, 'chess/server/client.py', '<pre contenteditable="false">import socket\r\n\r\nimport sys\r\nsys.path.append(''../'')\r\n\r\nfrom engine.headers import Turn, Coordinates\r\nfrom engine.board import Board\r\nfrom gui.game import Game\r\n\r\nimport asyncio\r\n        \r\n\r\nclass SocketClient(asyncio.Protocol, Board):\r\n    def __init__(self, message, on_con_lost):\r\n        self.message = message\r\n        self.on_con_lost = on_con_lost\r\n\r\n        Board.__init__(self)\r\n        self.set_defautl_board()\r\n        game = Game(self)\r\n        game.start_game()\r\n\r\n    def connection_made(self, transport):\r\n        self.transport = transport\r\n        transport.write(self.message.encode())\r\n        print(''Data sent: {!r}''.format(self.message))\r\n\r\n    def drag_figure(self, figure, new_coordinate: Coordinates, is_castling=False):\r\n        super().drag_figure(figure, new_coordinate, is_castling)\r\n        self.write_data(figure.name.encode())\r\n\r\n    def write_data(self, message):\r\n        self.transport.write(message)\r\n\r\n    def data_received(self, data):\r\n        print(''Data received: {!r}''.format(data.decode()))\r\n\r\n    def connection_lost(self, exc):\r\n        print(''The server closed the connection'')\r\n        self.on_con_lost.set_result(True)\r\n\r\n    def send_move(self, figure, new_coordinate: Coordinates):\r\n        self.socket.sendall(f''move\\n{self.figure.type_}\\n{figure.name}\\nx={figure.position.x} y={figure.position.y}\\nx={new_coordinate.x} y={new_coordinate.y}'')\r\n\r\nasync def start_client():\r\n    loop = asyncio.get_running_loop()\r\n\r\n    on_con_lost = loop.create_future()\r\n    message = ''H''\r\n\r\n    transport, protocol = await loop.create_connection(\r\n        lambda: SocketClient(message, on_con_lost),\r\n        ''127.0.0.1'', 15555)\r\n\r\n    # Wait until the protocol signals that the connection\r\n    # is lost and close the transport.\r\n    try:\r\n        await on_con_lost\r\n    finally:\r\n        transport.close()\r\n    \r\ndef init_client():\r\n    asyncio.run(start_client())\r\n</pre>', '2024-06-17 20:31:49', '2024-06-17 20:31:49', 0, 0, 0, 1, 0, '', '', 0, -1),
(255, 0, 13, 255, 1, 'chess/server/server.py', '<pre contenteditable="false">import socket\r\nimport asyncio\r\n\r\n\r\nasync def handle_client(client):\r\n    loop = asyncio.get_event_loop()\r\n    request = None\r\n    await loop.sock_sendall(client, ''Start''.encode(''utf8''))\r\n    while request != ''quit'':\r\n        request = (await loop.sock_recv(client, 255)).decode(''utf8'')\r\n        response = str(request)\r\n        await loop.sock_sendall(client, response.encode(''utf8''))\r\n    client.close()\r\n\r\nasync def run_server():\r\n    server = socket.socket(socket.AF_INET, socket.SOCK_STREAM)\r\n    server.bind((''localhost'', 15555))\r\n    server.listen(8)\r\n    server.setblocking(False)\r\n\r\n    loop = asyncio.get_event_loop()\r\n\r\n    while True:\r\n        client, _ = await loop.sock_accept(server)\r\n        loop.create_task(handle_client(client))\r\n\r\nasyncio.run(run_server())\r\n</pre>', '2024-06-17 20:32:03', '2024-06-17 20:32:03', 0, 0, 0, 1, 0, '', '', 0, -1),
