(270, 0, 14, 270, 1, 'gomeltrans_api/api/views.py', '<pre contenteditable="false">from django.conf import settings\r\nfrom django.http import JsonResponse\r\n\r\nfrom .utils import load_data_from_json\r\nfrom .misc import get_route_from_stops, sort_nearest_routes\r\n\r\nfrom datetime import datetime\r\nimport functools\r\n\r\n\r\ndef get_route(request, type_transport: str, number: str):\r\n    response = {''response'': None, ''status_code'': 200}\r\n\r\n    if type_transport not in settings.TRANSPORT_TYPES:\r\n        response[''status_code''] = 404\r\n        return JsonResponse(response)\r\n    try:\r\n        response[''response''] = load_data_from_json(type_transport)[number]\r\n    except KeyError:\r\n        response[''status_code''] = 404\r\n\r\n    return JsonResponse(response, safe=True)\r\n\r\n\r\ndef get_all_name_routes(request, type_transport: str):\r\n    data = list(load_data_from_json(type_transport).keys())\r\n    response = {''response'': data, ''status_code'': 200}\r\n\r\n    return JsonResponse(response, safe=True)\r\n\r\n\r\ndef get_route_from_stops(request):\r\n    stop_from = request.GET.get(''from'', '''')\r\n    stop_to = request.GET.get(''to'', '''')\r\n\r\n    routes = functools.reduce(lambda a, b: a + b, [get_route_from_stops(transport_type, stop_from, stop_to) for transport_type in settings.TRANSPORT_TYPES])\r\n\r\n    response = {''response'': routes, ''status_code'': 200 if routes else 404}\r\n\r\n    return JsonResponse(response, safe=True)\r\n\r\n\r\ndef get_nearest_route(reqeust):\r\n    stop_from = reqeust.GET.get(''from'', '''')\r\n    stop_to = reqeust.GET.get(''to'', '''')\r\n\r\n    get_nearest_route = lambda type_transport: sort_nearest_routes(get_route_from_stops(type_transport, stop_from, stop_to), stop_from)\r\n\r\n    bus_nearest_route = get_nearest_route(''bus'')\r\n    trolleybus_nearest_route = get_nearest_route(''trolleybus'')\r\n    \r\n\r\n    nearest_route = bus_nearest_route if bus_nearest_route and trolleybus_nearest_route and datetime.strptime(bus_nearest_route.get(''stop_time''), "%H:%M") &lt; datetime.strptime(trolleybus_nearest_route.get(''stop_time''), "%H:%M") else trolleybus_nearest_route if trolleybus_nearest_route else bus_nearest_route if bus_nearest_route else None\r\n\r\n    response = {''response'': nearest_route, ''status_code'': 200 if nearest_route else 404}\r\n\r\n    return JsonResponse(response, safe=True)\r\n    </pre>', '2024-06-18 18:03:43', '2024-06-18 18:03:43', 0, 0, 0, 1, 0, '', '', 0, -1),
