(290, 0, 16, 290, 1, 'mood/core/forms.py', '<pre contenteditable="false">from django import forms\r\nfrom django.contrib.auth.forms import UserCreationForm, AuthenticationForm\r\nfrom django.contrib.auth.models import User\r\nfrom .models import Profile\r\n\r\n\r\nclass RegiserUserForm(UserCreationForm):\r\n    username = forms.CharField(\r\n        label=''Логин'', \r\n        widget=forms.TextInput(attrs={''class'': ''form-control''}))\r\n\r\n    email = forms.EmailField(\r\n        label=''Email'', \r\n        widget=forms.EmailInput(attrs={''class'': ''form-control''}))\r\n\r\n    password1 = forms.CharField(\r\n        label=''Пароль'', \r\n        widget=forms.TextInput(attrs={''class'': ''form-control'', ''type'': ''password''}))\r\n\r\n    password2 = forms.CharField(\r\n        label=''Пароль'', \r\n        widget=forms.TextInput(attrs={''class'': ''form-control'', ''type'': ''password''}))\r\n\r\n    class Meta:\r\n        model = User\r\n        fields = (''username'', ''email'', ''password1'', ''password2'')\r\n\r\n\r\nclass LoginUserForm(AuthenticationForm):\r\n    username = forms.CharField(label=''Логин'', \r\n    widget=forms.TextInput(attrs={''class'': ''form-control''}))\r\n\r\n    password = forms.CharField(label=''Пароль'', \r\n    widget=forms.TextInput(attrs={''class'': ''form-control'', ''type'': ''password''}))\r\n\r\n\r\nclass EditProfileForm(forms.ModelForm):\r\n    first_name = forms.CharField(\r\n                    label=''Имя'', \r\n                    max_length=30,\r\n                    widget=forms.TextInput(attrs={''class'': ''form-control'', ''id'': ''exampleFormControlInput1'', ''placeholder'': ''Имя'', ''name'': ''first_name''}),\r\n                    required=False\r\n                    )\r\n    surname = forms.CharField(\r\n                    label=''Фамилия'', \r\n                    max_length=40,\r\n                    widget=forms.TextInput(attrs={''class'': ''form-control'', ''id'': ''exampleFormControlInput1'', ''placeholder'': ''Фамилия'', ''name'': ''surname''}),\r\n                    required=False\r\n                    )\r\n    avatar = forms.FileField(\r\n                    label=''Аватарка'', \r\n                    widget=forms.FileInput(attrs={''class'': ''form-control'', ''id'': ''exampleFormControlInput1'', ''placeholder'': ''Аватарка'', ''name'': ''avatar''}),\r\n                    required=False\r\n                    )\r\n    vk_social_link = forms.CharField(\r\n                        label=''VK'',\r\n                        max_length=150,\r\n                        widget=forms.TextInput(attrs={''class'': ''form-control'', ''id'': ''exampleFormControlInput1'', ''placeholder'': ''Ссылка на VK'', ''name'': ''vk_social_link''}),\r\n                        required=False\r\n                    )\r\n    github_social_link = forms.CharField(\r\n                            label=''Github'',\r\n                            max_length=150,\r\n                            widget=forms.TextInput(attrs={''class'': ''form-control'', ''id'': ''exampleFormControlInput1'', ''placeholder'': ''Ссылка на VK'', ''name'': ''vk_social_link''}),\r\n                            required=False\r\n                    )\r\n    interest = forms.CharField(\r\n                            label=''Интересы'',\r\n                            max_length=40,\r\n                            widget=forms.TextInput(attrs={''class'': ''form-control'', ''id'': ''exampleFormControlInput1'', ''placeholder'': ''Введите свои интересы'', ''name'': ''interests''}),\r\n                            required=False\r\n    )\r\n\r\n    class Meta:\r\n        model = Profile\r\n        fields = (''first_name'', ''surname'', ''avatar'', ''vk_social_link'', ''github_social_link'')\r\n\r\n</pre>', '2024-06-18 19:21:13', '2024-06-18 19:21:13', 0, 0, 0, 1, 0, '', '', 0, -1),
(291, 0, 16, 291, 1, 'mood/core/models.py', '<pre contenteditable="false">from django.db import models\r\nfrom django.contrib.auth.models import User\r\nfrom .utils import validate_profile_avatar, validate_vk_link, validate_github_link\r\n\r\n\r\nUser._meta.get_field(''email'')._unique = True\r\n\r\n\r\nclass Profile(models.Model):\r\n    user = models.OneToOneField(User, on_delete=models.CASCADE)\r\n    first_name = models.CharField(max_length=30, null=True)\r\n    surname = models.CharField(max_length=30, null=True)\r\n    avatar = models.ImageField(upload_to=validate_profile_avatar, null=True)\r\n    vk_social_link = models.CharField(max_length=150, null=True, validators=[validate_vk_link], verbose_name=''Ссылка на VK'')\r\n    github_social_link = models.CharField(max_length=150, null=True, validators=[validate_github_link], verbose_name=''Ссылка на Github'')\r\n    subscribers = models.ManyToManyField(User, related_name=''subscribers'')\r\n\r\n\r\nclass Interest(models.Model):\r\n    title = models.CharField(max_length=25, null=True, verbose_name=''Строчка интереса'')\r\n    user = models.ForeignKey(Profile, on_delete=models.CASCADE)\r\n</pre>', '2024-06-18 19:33:00', '2024-06-18 19:33:00', 0, 0, 0, 1, 0, '', '', 0, -1),
