(361, 0, 17, 361, 1, 'core/library/query.py', '<pre contenteditable="false">import requests\r\n\r\n\r\nclass Query:\r\n	@staticmethod\r\n	def get_request(url, params, variables):\r\n		"""\r\n		HTTP запрос GET\r\n		url - ссылка\r\n		params - параметры запроса\r\n		"""\r\n		if params != ''bad'':\r\n			response = requests.get(url=eval(url, variables),params=eval(params, variables))\r\n		else:\r\n			response = requests.get(url=eval(url, variables))\r\n		return eval(response.text)\r\n\r\n	@staticmethod\r\n	def post_request(url, params, variables):\r\n		"""\r\n		HTTP запрос POST\r\n		url - ссылка\r\n		params - параметры запроса\r\n		"""\r\n		if params != ''bad'':\r\n			response = requests.post(url=eval(url, variables),params=eval(params, variables))\r\n		else:\r\n			response = requests.post(url=eval(url, variables))\r\n		return response.text\r\n\r\n\r\n</pre>', '2024-06-19 16:59:47', '2024-06-19 16:59:47', 0, 0, 0, 1, 0, '', '', 0, -1),
(362, 0, 17, 362, 1, 'core/library/robot.py', '<pre contenteditable="false">import pygame\r\nimport time\r\n\r\n\r\nclass Task:\r\n    def __init__(self, pointPos, countTiles, tilesSize, bricksPos, playerPos, paintPos, pointImagePos):\r\n        self.pointPos = pointPos\r\n        self.countTiles = countTiles\r\n        self.tilesSize = tilesSize\r\n        self.bricksPos = bricksPos\r\n        self.playerPos = playerPos\r\n        self.paintPos = paintPos\r\n        self.pointImagePos = pointImagePos\r\n\r\nclass Robot:\r\n    def __init__(self):\r\n        self.move_items = {\r\n            ''Left'': (-1, 0),\r\n            ''Right'': (+1,0),\r\n            ''Up'': (0,-1),\r\n            ''Down'': (0,+1),\r\n            ''Paint'': ''p''\r\n        }\r\n        self.tasks = {\r\n            ''Task1'' : Task(pointPos=(3,0),\r\n                            countTiles=(4,2),\r\n                            tilesSize=200,\r\n                            bricksPos=[(2,1),(0,0)],\r\n                            playerPos=(0,1),\r\n                            paintPos=[(1,1),(1,0),(2,0)],\r\n                            pointImagePos=(7,1)),\r\n            ''Task2'': Task(pointPos=(0, 0),\r\n                          countTiles=(7, 4),\r\n                          tilesSize=200,\r\n                          bricksPos=[(0, 1), (1, 1), [2, 1], [3, 1], [4, 1]],\r\n                          playerPos=(1, 2),\r\n                          paintPos=[(1, 0), (2, 0), (3, 0), (4, 0)],\r\n                          pointImagePos=(1,1)),\r\n            ''Task3'': Task(pointPos=(0, 0),\r\n                          countTiles=(6,3),\r\n                          tilesSize=200,\r\n                          bricksPos=[(1,2),(3,2),(3,1),(5,1)],\r\n                          playerPos=(0,2),\r\n                          paintPos=[(1,0),(2,2),(2,1),(5,2),(4,2)],\r\n                          pointImagePos=(1,1)),\r\n            ''Task4'': Task(pointPos=(0,0),\r\n                          countTiles=(4,3),\r\n                          tilesSize=200,\r\n                          bricksPos=[(0,2),(0,1),(2,1)],\r\n                          playerPos=(1,0),\r\n                          paintPos=[(2,0),(3,0),(3,1),(3,2),(2,2),(1,2),(1,1)],\r\n                          pointImagePos=(1,1)),\r\n            ''Task5'': Task(pointPos=(7, 0),\r\n                          countTiles=(7, 2),\r\n                          tilesSize=200,\r\n                          bricksPos=[(1, 0), (3, 1), (5, 0)],\r\n                          playerPos=(0, 0),\r\n                          paintPos=[(0, 1), (2, 1), (3, 0), (4, 1), (6, 1)],\r\n                          pointImagePos=(13, 1)\r\n                          )\r\n        }\r\n\r\n        self.moves = []\r\n\r\n        print(''Привет!\\nЭто EquilibriumRobot, и вот список команд для прохождения уровней:\\n - task&lt;number&gt; - запустить определённый уровень\\n - Right - идти направо\\n - Left - идти налево\\n - Top - идти вверх\\n - Down - идти вниз\\n - Paint - закрасить\\n - end - для запуска уровня\\n\\nДля прохождения введите команды и нажимайте W\\n------------------'')\r\n\r\n    def route_move(self, type_move):\r\n        type_move = type_move.title()\r\n        if type_move.startswith(''Task''):\r\n            self.task = self.tasks[type_move]\r\n        elif type_move == ''End'':\r\n            self.run_app()\r\n        else:\r\n            if ''*'' in type_move:\r\n                type_move = type_move.split(''*'')\r\n                for el in range(int(type_move[1])):\r\n                    self.moves.append(self.move_items[type_move[0].replace('' '','''')])\r\n            else:\r\n                self.moves.append(self.move_items[type_move.title()])\r\n\r\n    def is_out_border(self, x_player, y_player, x_move, y_move, game_res, tile_size):\r\n        """\r\n        Функция возвращает, переходит ли игрок границу или стену\r\n        """\r\n        if x_player + x_move &gt; game_res[0] or x_player + x_move &lt; 0:\r\n            return False\r\n        elif y_player + y_move &gt;= game_res[1] or y_player + y_move &lt; 0:\r\n            return False\r\n        for brickPos in self.task.bricksPos:\r\n            if y_player + y_move == brickPos[1] * tile_size and x_player + x_move == brickPos[0] * tile_size:\r\n                return False\r\n        return True\r\n\r\n    def run_app(self):\r\n        WIDTH, HEIGHT = self.task.countTiles # количество плиток\r\n        TILE = self.task.tilesSize # размер плитки\r\n        GAME_RES = WIDTH * TILE, HEIGHT * TILE # разрешения экрана\r\n        FPS = 30\r\n\r\n        pygame.init()\r\n        screen = pygame.display.set_mode(GAME_RES)\r\n        pygame.display.set_caption(''EquilibriumRobot'')\r\n        pygame.display.set_icon(pygame.image.load("../core/library/static/icon.png"))\r\n        # отрисовка фона\r\n        grid = [pygame.Rect(x * TILE , y * TILE, TILE, TILE ) for x in range(WIDTH) for y in range(HEIGHT)]\r\n        clock = pygame.time.Clock()\r\n        # позиция игрока\r\n        x_player, y_player = self.task.playerPos[0] * TILE, self.task.playerPos[1] * TILE\r\n        count_moves = 0\r\n        # цвета\r\n        grid_color = (40,40,40)\r\n        paint_color = (255, 255, 0)\r\n\r\n        len_paints = len(self.task.paintPos)\r\n\r\n        point_image = pygame.image.load(''../core/library/static/point.png'')\r\n        brick_image = pygame.image.load(''../core/library/static/brick.png'')\r\n        paint_image = pygame.image.load(''../core/library/static/paint.png'')\r\n        player_image = pygame.image.load(''../core/library/static/player.png'')\r\n\r\n        while True:\r\n            pressed_keys = pygame.key.get_pressed()\r\n            # поля\r\n            [pygame.draw.rect(screen, grid_color, i_rect, 1) for i_rect in grid]\r\n            # игрок\r\n            player = player_image.get_rect(center=(x_player + 100, y_player + 100))\r\n            screen.blit(player_image,player)\r\n            # цель\r\n      