(368, 0, 17, 368, 1, 'core/equilibrium.py', '<pre contenteditable="false">""" Файл для сборки eq файла """\r\nimport argparse\r\nimport sys\r\nfrom lexer import Lexer\r\nimport parserr\r\nimport exceptions\r\nfrom datetime import datetime\r\nimport time\r\n\r\n\r\ncli = argparse.ArgumentParser(description=''Equilibrium'')\r\ncli.add_argument("--source", default=''code.eq'', type=str)\r\ncli.add_argument("--interactive", default=0, type=int)\r\ncli.add_argument("--compile", default=False, type=bool)\r\ncli.add_argument("--speed", default=False, type=bool)\r\ncli.add_argument("--support", default=False, type=bool)\r\n\r\nclass CodeReader:\r\n    def __init__(self, source, level):\r\n        if level == ''PROD'':\r\n            self.all_code = []\r\n            with open(source,encoding="utf-8") as source:\r\n                self.all_code = [line.strip() for line in source]\r\n        elif level == ''DEBUG'':\r\n            self.all_code = source\r\n\r\n    def get_code(self):\r\n        return self.all_code\r\n\r\nclass Equilibrium:\r\n    """ Основной класс """\r\n    def __init__(self, source, level):\r\n        self.source = source\r\n        self.level = level\r\n\r\n    def run_code(self):\r\n        """ Функция для запуска кода """\r\n        code_read = CodeReader(self.source, self.level).get_code() # код разбитый по лайнам\r\n        lex = Lexer(code_read) # лексер, возвращающий лексемы\r\n        parser = parserr.Parser() # парсер, обрабатывающий логику\r\n        parser.parser(lex.lexer())\r\n\r\nclass SpeedTest:\r\n    def __init__(self, source):\r\n        self.source = source\r\n\r\n    def analysis_speed(self):\r\n        before_start = time.time()\r\n        eq = Equilibrium(self.source, ''PROD'')\r\n        eq.run_code()\r\n        after_end = time.time()\r\n        return datetime.utcfromtimestamp(after_end - before_start).strftime(''%H часов %M минут %S секунд %f микросекунд'')\r\n\r\ndef route_args(args):\r\n    """\r\n    Функция отвечате за режимы сборки программы на Equilibrium\r\n    --compile True/False default - компилируемый режим, где нужно указать путь к файлу в консоле\r\n    --interactive 0 default/1/2 - интерактивный режим,1 - режим когда ты пишешь команды, а когда\r\n    хочешь запустить их на выполения пишешь go/run.2 - каждая команда запускается на выполнения\r\n    автоматически\r\n    --source - обычное выполнение программы, для запуска нужно указать путь к файлу с .eq\r\n    default значения - code.eq\r\n    --speed - если True, то выводит скорость выполнения программы из source\r\n    default значения - False\r\n    """\r\n    if args.support:\r\n        help_string = """\r\nПривет, это справочная информация об языке Equilibrium\r\n------------------------------------------------------\r\nМетоды:\r\nwrite - вывод информации в консоль\r\nsleep - ожидания определённое количество секунд\r\ncfile - создания файла\r\nwfile - запись файла\r\n        """\r\n        print(help_string)\r\n    if args.speed:\r\n        speed_test = SpeedTest(args.source)\r\n        print(f''Программа отработала за {speed_test.analysis_speed()}'')\r\n    if args.source:\r\n        eq = Equilibrium(args.source,''PROD'')\r\n        eq.run_code()\r\n    elif args.compile:\r\n        print(''Привет,это компилируемый режим Equilibrium'')\r\n        way_to_file = input(''Введи путь к файлу: '')\r\n        eq = Equilibrium(way_to_file, ''PROD'')\r\n        eq.run_code()\r\n    elif args.interactive:\r\n        print(''Привет!\\nЭто интерактивный режим Equilibrium\\nВводи команды и когда захочешь запустить программу напиши go'')\r\n        command_list = []\r\n        while True:\r\n            command = input(''\\n&gt;&gt;&gt; '')\r\n            \r\n            if command.lower() in (''exit'',''quit''):\r\n                sys.exit()\r\n            if args.interactive == 1:\r\n                if command.lower() in (''run'', ''go''):\r\n                    Eq = Equilibrium(command_list, ''DEBUG'')\r\n                    Eq.run_code()\r\n                else:\r\n                    command_list.append(command)\r\n            elif args.interactive == 1:\r\n                eq = Equilibrium([command], ''DEBUG'')\r\n                eq.run_code()\r\n            else:\r\n                exceptions.BuildError(''Ошибка сборки'')\r\n\r\n\r\ntry:\r\n    args = cli.parse_args()\r\n    try:\r\n        a = args.source.split(''.'')[1] != ''eq''\r\n    except IndexError:\r\n        exceptions.FileNoEquilibrium(''Файл не имеет расширение .eq'')\r\n\r\n    route_args(args)\r\nexcept FileNotFoundError:\r\n    print(''Такого файла нет!'')\r\nexcept KeyboardInterrupt:\r\n    print(''\\nПрограмма завершена!'')\r\nexcept Exception as e:\r\n    print(e)\r\n    print(''Ошибка..'')</pre>', '2024-06-19 17:01:41', '2024-06-19 17:01:41', 0, 0, 0, 1, 0, '', '', 0, -1),
(369, 0, 17, 369, 1, 'core/exceptions.py', '<pre contenteditable="false">"""\r\nФайл Exceptions\r\nОбработка ошибок\r\n"""\r\nimport sys\r\n\r\n\r\nclass Lexer_Error:\r\n	def __init__(self, msg, line_warning):\r\n		print(f''{msg}\\n{line_warning}'')\r\n		exit()\r\n\r\n\r\nclass Excp:\r\n	def __init__(self, msg):\r\n		print(f''\\nОшибка - {msg}'')\r\n		sys.exit()\r\n\r\n\r\nclass Value_Error(Excp):\r\n	pass\r\n\r\n\r\nclass Parser_Error(Excp):\r\n	pass\r\n\r\n\r\nclass Type_Error(Excp):\r\n	pass\r\n\r\n\r\nclass Index_Error(Excp):\r\n	pass\r\n\r\n\r\nclass Syntax_Error(Excp):\r\n	pass\r\n\r\n\r\nclass Key_Error(Excp):\r\n	pass\r\n\r\n\r\nclass File_Exists(Excp):\r\n	pass\r\n\r\n\r\nclass OS_Error(Excp):\r\n	pass\r\n\r\n\r\nclass Zero_Error(Excp):\r\n	pass\r\n\r\n\r\nclass File_Connection(Excp):\r\n	pass\r\n\r\nclass Name_Error(Excp):\r\n	pass\r\n\r\nclass FileNoEquilibrium(Excp):\r\n	pass\r\n\r\nclass LibraryClassNoImport(Excp):\r\n	pass\r\n\r\nclass MicroserviceError(Excp):\r\n	pass\r\n\r\nclass BuildError(Excp):\r\n	pass</pre>', '2024-06-19 17:01:54', '2024-06-19 17:01:54', 0, 0, 0, 1, 0, '', '', 0, -1),
(370, 0, 17, 370, 1, 'core/lexer.py', '<pre contenteditable="false">"""\r\nФайл лексера, который разбивает файл на лексемы\r\nне обрабатывая их логику\r\n"""\r\nimport exceptions as excp\r\nimport re\r\n\r\n\r\nclass Lexer:\r\n    """ Класс лексера """\r\n    def __init__(self, source):\r\n        """ Получаем полайново код """\r\n        self.all_code = source\r\n        self.stack = []\r\n        self.lexemes_instructions = {\r\n            ''delete'': lambda line: [{''delete'': line.split('' '')[1]}],\r\n            ''def_'': lambda line: [{''def_'': line[4:].replace('' '', '''')}],\r\n            ''include'': lambda line: [{''include'': line.split('' '')[1]}],\r\n            ''else'': lambda line: [{''else'': line.split('' '')[1][:-1]}],\r\n            ''}'': lambda line: [{''end_if'': 0}],\r\n            ''if'': lambda line: [{''if'': line[3:-1]}],\r\n            ''run'': lambda line: [{''run'': line[4::]}],\r\n            ''elif'': lambda line: [{''elif'': line[5:-1]}],\r\n            ''range'': lambda line: [{''range'': line[6:-1]}],\r\n            ''for'': lambda line: [{''for_'' + line.split(''for'')[1].split('','')[0].replace(''('', '''').replace('' '', '''') + '' '' + line.split('','')[1]: line.split('','')[2].split('')'')[0] + '' '' + line.split('')'')[1][:-1].replace('' '', '''')}],\r\n            ''def'': lambda line: [{''def'': line.split(''('')[0].split('' '')[1] + '' '' + line.split('' '')[-1][:-1] + '' '' + line.split(''('')[1].split('')'')[0]}],\r\n            ''import'': lambda line: [{''import'': line[7:]}],\r\n            ''quit'': lambda line: [{''quit'': ''quit''}],\r\n            ''while'': lambda line: [{f''while_{line[-3:-1]}'': line[6:-3]}],\r\n            ''do'': lambda line: [{f''do while_{line[-3:-1]}'': line[9:-3]}],\r\n            ''use'': lambda line: [{''use'': line[4:]}]\r\n        }\r\n\r\n    def lexer(self):\r\n        """ Разбиваем на лексемы """\r\n        for line in self.all_code:\r\n            try:\r\n                line = line.lstrip('' '').rstrip('' '').lstrip(''\\t'').rstrip(''\\t'')\r\n                if line.split('' '')[0] in self.lexemes_instructions:\r\n                    self.stack.extend(self.lexemes_instructions[line.split('' '')[0]](line))\r\n                elif line.startswith(''ufather?''):\r\n                    print(''Renat Yakublevich'')\r\n                elif re.findall(r''\\S+ =&gt;'',line) or re.findall(r''\\S+=&gt;'',line):  # функции\r\n                    self.stack += [{''f_'' + line.split(''=&gt;'')[0].replace('' '',''''): ''''.join(line.split(''=&gt;'')[1::])[1::]}]\r\n                elif re.findall(''\\S+ \\S+ :='',line) or re.findall(''\\S+ \\S+:='',line):  # переменные\r\n                    if line.split('' '')[1].find(''+'') != -1:\r\n                        self.stack += [\r\n                            {''variable_'' + line.split('' '')[0] + ''_'' + line.split('' '')[1].split(''+'')[1]: ''''.join(line.split('':='')[1::])[1::]}]\r\n                    else:\r\n                        self.stack += [{''variable_'' + line.split('' '')[0] + ''_'' + line.split('' '')[1]: ''''.join(line.split('':='')[1::])[1::]}]\r\n                elif not line or line.startswith(''&gt;''):\r\n                    continue\r\n                else:\r\n                    excp.Lexer_Error(''Строка не понятна интерпритатору'',line)\r\n            except IndexError as e:\r\n                print(e)\r\n\r\n        return self.stack</pre>', '2024-06-19 17:02:11', '2024-06-19 17:02:11', 0, 0, 0, 1, 0, '', '', 0, -1),
