(391, 0, 17, 391, 1, 'microservices_sources/sheets/url_short.py', '<pre contenteditable="false">import os\r\nimport sqlite3\r\nfrom flask import Flask, redirect, request, render_template, flash, g, url_for, jsonify\r\nfrom url_short_db import Database\r\nimport requests\r\nimport logging\r\n\r\n\r\nlogging.basicConfig(level=logging.DEBUG)\r\n\r\nDATABASE_FILE = ''db.db''\r\nDEBUG = True\r\nSECRET_KEY = ''xzf39d7348yfui''\r\nHOST = ''http://127.0.0.1''\r\nPORT = ''4997''\r\n\r\napp = Flask(__name__)\r\napp.config.from_object(__name__)\r\n\r\ndef error_handler(func):\r\n	def wrapper(*args,**kwargs):\r\n		try:\r\n			return func(*args,**kwargs)\r\n		except Exception as e:\r\n			logging.error(f''Some error!\\n{e}'')\r\n\r\n	wrapper.__name__ = func.__name__\r\n	return wrapper\r\n\r\ndef connect_db():\r\n	conn = sqlite3.connect(app.config[''DATABASE_FILE''])\r\n	conn.row_factory = sqlite3.Row\r\n	return conn\r\n\r\ndef get_db():\r\n	if not hasattr(g, ''link_db''):\r\n		g.link_db = connect_db()\r\n	return g.link_db\r\n\r\n@error_handler\r\ndef validation_link(link: str) -&gt; bool:\r\n	if link.startswith(''https://''):\r\n		return True\r\n\r\n@app.route(''/'',methods=[''POST'',''GET''])\r\n@error_handler\r\ndef index():\r\n	if request.method == ''POST'':\r\n		if validation_link(request.form[''link'']):\r\n			requests.post(f''{HOST}:{PORT}{url_for("add_link")}'',params={''link'': request.form[''link''], ''redirect_link'': request.form[''rlink'']})\r\n			flash(''Ссылка успешно добавлена!'', category=''succes'')\r\n		else:\r\n			flash(''Ошибка добавления'', category=''error'')\r\n\r\n	return render_template(''index.html'',title=''Главная'')\r\n\r\n@app.route(''/&lt;path:link&gt;'')\r\n@error_handler\r\ndef get_link(link):\r\n	try:\r\n		db = get_db()\r\n		dbase = Database(db)\r\n		return redirect(dbase.get_link(link)[''true_url''])\r\n	except TypeError:\r\n		return ''Такой ссылки нет!''\r\n\r\n@app.route(''/create_link'',methods=[''POST'',''GET''])\r\ndef add_link():\r\n	db = get_db()\r\n	dbase = Database(db)\r\n	dbase.add_link(request.args.get(''link''),request.args.get(''redirect_link''))\r\n	return jsonify([{''redirect_link'':request.args.get(''redirect_link'')}])\r\n\r\nif __name__ == ''__main__'': \r\n    app.run(port=PORT,debug=DEBUG)</pre>', '2024-06-19 17:09:21', '2024-06-19 17:09:21', 0, 0, 0, 1, 0, '', '', 0, -1),
(392, 0, 17, 392, 1, 'microservices_sources/sheets/url_short_db.py', '<pre contenteditable="false">import sqlite3\r\n\r\n\r\nclass Database:\r\n    def __init__(self,database_file):\r\n        self.connection = database_file\r\n        self.cursor = database_file.cursor()\r\n\r\n    def add_link(self, link, redirect_link):\r\n        self.cursor.execute("INSERT INTO `links` (`true_url`, `redirect_url`) VALUES(?,?)",(link, redirect_link))\r\n        self.connection.commit()\r\n\r\n    def get_link(self, redirect_link):\r\n        return self.cursor.execute(''SELECT `true_url` FROM `links` WHERE `redirect_url` = ?'',(redirect_link,)).fetchone()\r\n\r\n\r\n</pre>', '2024-06-19 17:09:34', '2024-06-19 17:09:34', 0, 0, 0, 1, 0, '', '', 0, -1),
