(426, 0, 19, 426, 1, 'core/views.py', '<pre contenteditable="false">from django.shortcuts import render, redirect\r\nfrom core.models import Article, Comment, Category\r\nfrom django.core.paginator import Paginator\r\nimport core.utils as utils\r\n\r\n\r\ndef index(request):\r\n    context = {}\r\n    articles = Article.objects.all()\r\n    paginator = Paginator(articles, 5)\r\n\r\n    page_number = request.GET.get(''page'', 1)\r\n    context[''articles''] = paginator.get_page(page_number)\r\n\r\n    return render(request, ''index.html'', context=context)\r\n\r\n\r\ndef get_article(request, id):\r\n    try:\r\n        if request.method == "GET":\r\n            context = {}\r\n            article = Article.objects.get(id=id)\r\n            context[''article''] = article\r\n            comments = Comment.objects.filter(article=article).all()\r\n            context[''comments''] = reversed(comments)\r\n            return render(request, ''article.html'', context=context)\r\n        elif request.method == "POST":\r\n            context = {}\r\n\r\n            comment = request.POST.get(''comment'')\r\n            comment = Comment(text=comment, article=Article.objects.get(id=id))\r\n            comment.save()\r\n            \r\n            article = Article.objects.get(id=id)\r\n            context[''article''] = article\r\n            comments = Comment.objects.filter(article=article).all()\r\n            context[''comments''] = reversed(comments)\r\n            request.method = "GET"\r\n            return render(request, ''article.html'', context=context) \r\n            \r\n    except Article.DoesNotExist:\r\n        context = {}\r\n        context[''error''] = ''Такой записи не существует''\r\n        return render(request, ''article.html'', context=context)\r\n\r\n\r\ndef get_all_articles(request):\r\n    context = {}\r\n    context[''articles''] = Article.objects.all()\r\n    return render(request, ''all_articles.html'', context=context)\r\n\r\n\r\ndef search_article(request):\r\n    context = {}\r\n    if request.method == ''POST'':\r\n        search_text = request.POST.get(''search_text'')\r\n        context[''articles''] = utils.search(Article, search_text)\r\n        context[''search_text''] = search_text\r\n    return render(request, ''search.html'', context=context)\r\n\r\n\r\ndef get_all_categorys(request):\r\n    context = {}\r\n    context[''categorys''] = Category.objects.all()\r\n    return render(request, ''categorys.html'', context=context)\r\n\r\n\r\ndef get_category(request, name):\r\n    context = {}\r\n    context[''all_articles''] = Article.objects.filter(category__name__contains=name).all()\r\n    return render(request, ''category.html'', context=context)</pre>', '2024-06-19 18:49:56', '2024-06-19 18:49:56', 0, 0, 0, 1, 0, '', '', 0, -1),
